import React, { Fragment, useEffect, useState } from "react";
import "./styles.css";
import axios from "axios";
import { connect } from "react-redux";
import { withRouter } from "react-router-dom";
import { Container, Row, Col, Card, Media, TabContent, TabPane, Nav, NavItem, NavLink, CardBody, CardHeader, ButtonGroup, Button } from 'reactstrap';


const ViewHiredHackersHelper = ({ userData }) => {

    const [ hackers, setHackersData ] = useState([]);
    const [ activeTab, setActiveTab ] = useState("1");

    useEffect(() => {
        const configuration = {
            params: {
                uniqueId: userData.uniqueId
            }
        }
        axios.get(`${process.env.REACT_APP_BASE_URL}/gather/active/jobs/employer/account`, configuration).then((res) => {
            if (res.data.message === "Successfully gathered active/hired hackers!") {
                console.log("Successfully gathered active/hired hackers!", res.data);

                const { hackers } = res.data;

                setHackersData(hackers);
            } else {
                console.log("ERROR gathering active/hired hackers...:", res.data);
            }
        }).catch((err) => {
            console.log("CRITICAL ERROR gathering active/hired hackers...:", err);
        })
    }, []);

    console.log("hired hackers state --- :", hackers);

    return (
        <Fragment>
            <Container fluid={true}>
                <Row>
                    <Col sm="12 sm-100" md="12 md-100" lg="12 lg-100" xl="12 xl-100">
                        <Card>
                            <CardHeader>
                                <h5>Active <strong>HIRED</strong> workers/hacker's that're still pending and actively employed.</h5>
                            </CardHeader>
                            <CardBody className="tabbed-card">
                                <Nav  className="nav-pills nav-secondary">
                                    <NavItem>
                                        <NavLink className={activeTab === '1' ? 'active' : ''} onClick={() => setActiveTab('1')}>
                                            <i className="icofont icofont-ui-home"></i> HOME  
                                        </NavLink>
                                    </NavItem>
                                    <NavItem>
                                        <NavLink className={activeTab === '2' ? 'active' : ''} onClick={() => setActiveTab('2')}>
                                            <i className="icofont icofont-man-in-glasses"></i> TWO
                                        </NavLink>
                                    </NavItem>
                                    <NavItem>
                                        <NavLink className={activeTab === '3' ? 'active' : ''} onClick={() => setActiveTab('3')}>
                                            <i className="icofont icofont-contacts"></i> THREE
                                        </NavLink>
                                    </NavItem>
                                </Nav>
                                <TabContent activeTab={activeTab}>
                                    <TabPane tabId="1">
                                        <p className="mb-0">{"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum"}</p>
                                    </TabPane>
                                    <TabPane tabId="2">
                                        <p className="mb-0">{"Lorem Ipsum is simply dummy , but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages"}</p>
                                    </TabPane>
                                    <TabPane tabId="3">
                                        <p className="mb-0">{"Lorem Ipsum is simply dummy text of the printing and typesetting industry. when an unknown printer took a galley of type and scrambled it to make a type specimen book.  recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum"}</p>
                                    </TabPane>
                                </TabContent>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, { })(withRouter(ViewHiredHackersHelper));
