import React, { Fragment, useEffect } from 'react';
import "./styles.css";
import { Row, Col, Media, Input, Label, Button, Form } from 'reactstrap';
import axios from 'axios';
import { connect } from "react-redux";
import { useForm, Controller } from 'react-hook-form';
import MainHooksCourseIndividualCustomHelpers from "./helpers/reduxFormHelper.js";

const checkMessageMeetsCritera = MainHooksCourseIndividualCustomHelpers().checkMessageMeetsCritera

const CommentsIndividualCourseHelper = ({ courseData, userData }) => {

    const { register, handleSubmit, control, resetField, getValues, setValue, setError, clearErrors, watch, formState: { errors }} = useForm({
        mode: "onTouched",
        reValidateMode: "onBlur"
        // delayError: 500
    });

    const currentValues = getValues();

    const leavePostComment = () => {
        console.log("leavePostComment ran...");

    }
    const onSubmitForm = (data, e) => {
        console.log("onSubmitForm submitted form properly...!:", data, e);

        // if (condition) {
        //     const configuration = {
        //         id: userData.uniqueId,
        //         courseId: courseData.id,
        //         comment: null
        //     }

        //     axios.post(`${process.env.REACT_APP_BASE_URL}/post/comment/course/individual/listing`, configuration).then((res) => {
        //         if (res.data.message === "Successfully posted comment!") {
        //             console.log(res.data);
        //         } else {
        //             console.log("Err", res.data);
        //         }
        //     }).catch((err) => {
        //         console.log(err);
        //     })
        // } else {

        // }
    }
    const renderFormErrors = (errors) => {
        console.log("renderFormErrors", errors);
    }
    return (
        <Fragment>
            <section className="comment-box">
                <h4>Comment's</h4>
                <Form onSubmit={handleSubmit(onSubmitForm, (errors) => renderFormErrors(errors))}>
                    <hr/>
                        <Label className={"heavy-label-course"}>{checkMessageMeetsCritera.label}</Label>
                        <Input {...checkMessageMeetsCritera.check(setError, register, clearErrors, "comment")} onChange={(e) => {
                            return checkMessageMeetsCritera.onChange(e, "comment", setValue);
                        }} value={currentValues.comment} name={"comment"} className={"form-control"} rows={6} type={"textarea"} placeholder={checkMessageMeetsCritera.placeholder} />
                        {errors.comment ? <span className="span-tooltip">{errors.comment.message}</span> : null}
                        <div className={"float-right-course-comment-btn"}>
                            <Button type={"submit"} className={"btn-square-primary"} color={"primary-2x"} outline>Leave/Post Comment!</Button>
                        </div>
                    <hr />
                </Form>
                <ul>
                <li>
                    <Media className="align-self-center"><Media className="align-self-center" src={require("../../../../../../../assets/images/blog/comment.jpg")} alt=""/>
                    <Media body>
                        <Row>
                        <Col md="4 xl-100">
                            <h6 className="mt-0">Name goes here<span> {"( Designer )"}</span></h6>
                        </Col>
                        <Col md="8 xl-100">
                            <ul className="comment-social float-left float-md-right learning-comment">
                            <li className="digits"><i className="icofont icofont-thumbs-up"></i>{"02 Hits"}</li>
                            <li className="digits"><i className="icofont icofont-ui-chat"></i>{"598 Comments"}</li>
                            </ul>
                        </Col>
                        </Row>
                        <p>{"There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum, you need to be sure there isn't anything embarrassing hidden in the middle of text."}</p>
                    </Media>
                    </Media>
                </li>
                <li>
                    <ul>
                    <li>
                        <Media><Media className="align-self-center" src={require("../../../../../../../assets/images/blog/9.jpg")} alt=""/>
                        <Media body>
                            <Row>
                            <Col xl="12">
                                <h6 className="mt-0">Name goes here<span> {"( Designer )"}</span></h6>
                            </Col>
                            </Row>
                            <p>{"There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum, you need to be sure there isn't anything embarrassing hidden in the middle of text."}</p>
                        </Media>
                        </Media>
                    </li>
                    </ul>
                </li>
                <li>
                    <Media><Media className="align-self-center" src={require("../../../../../../../assets/images/blog/4.jpg")} alt=""/>
                    <Media body>
                        <Row>
                        <Col md="4 xl-100">
                            <h6 className="mt-0">Name goes here<span> {"( Designer )"}</span></h6>
                        </Col>
                        <Col md="8 xl-100">
                            <ul className="comment-social float-left float-md-right learning-comment">
                            <li className="digits"><i className="icofont icofont-thumbs-up"></i>{"02 Hits"}</li>
                            <li className="digits"><i className="icofont icofont-ui-chat"></i>{"598 Comments"}</li>
                            </ul>
                        </Col>
                        </Row>
                        <p>{"There are many variations of passages of Lorem Ipsum available, but the majority have suffered alteration in some form, by injected humour, or randomised words which don't look even slightly believable. If you are going to use a passage of Lorem Ipsum, you need to be sure there isn't anything embarrassing hidden in the middle of text."}</p>
                    </Media>
                    </Media>
                </li>
                </ul>
            </section>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, {  })(CommentsIndividualCourseHelper);
