import React, { Fragment } from "react";
import ReactPlayer from "react-player";
import _ from "lodash";
import "./styles.css";
import { Modal } from 'react-responsive-modal';

const renderPictureOrVideoLast = (file) => {
    if (file !== null && _.has(file, "link")) {
        if (file.type.includes("video")) {
            // video logic
            return (
                <Fragment>
                    <li>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} className={"img-fluid"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        } else {
            // image logic
            return (
                <Fragment>
                    <li>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <img className="img-fluid" alt="post1" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        }  
    } else {
        // image logic - DEFAULT.
        return (
            <Fragment>
                <li>
                    <div className="latest-post profile-pic-video-sidebar-custom">
                        <img className="img-fluid" alt="post1" src={process.env.REACT_APP_PLACEHOLDER_IMAGE} />
                    </div>
                </li>
            </Fragment>
        );
    } 
}
const RenderGalleryModalHackerProfileHelper = ({ onCloseModal, isOpen }) => {
    return (
        <Fragment>
            <Modal open={isOpen} onClose={onCloseModal} center>
                <h2>Simple centered modal</h2>
            </Modal>
        </Fragment>
    );
}
export default {
    renderPictureOrVideoLast,
    RenderGalleryModalHackerProfileHelper
};
