import React, { Fragment, useState, useEffect } from 'react';
import Breadcrumb from '../../../../../../../layout/breadcrumb'
import { Container, Row, Col, Card, Media, CardHeader, CardBody, CardFooter, Button, TabContent, TabPane, Nav, NavItem, NavLink, Form, Label, Input } from 'reactstrap';
import TimelineTab from './helpers/timelineTab.js';
import AboutTab from './helpers/aboutTab.js';
import FriendsTab from './helpers/friendsTab.js';
import PhotosTab from './helpers/photosTab.js';
import { Timline, Friends, About, Photos, GeneralManager, COD, Fast, Free, Submit, Cancel, VerticalStyle, Local, XYZSeller, ABCSeller, Standard, DeliveryOption, BuyingOption } from "../../../../../../../constant";
import { useParams, withRouter } from 'react-router-dom';
import axios from 'axios';
import helpers from "./helpers/functions/helperFunctions.js";
import "./styles.css";
import _ from "lodash";


const { renderPictureOrVideoProfilePic, calculateFileType } = helpers;

const ManageApplicationIndividualHelper = ({ location }) => {
    // applicant
    const applicantData = location.state.applicant;
    // location props
    console.log("location viewIndividualInfo.js : ", location);
    // gather params
    const { id } = useParams();
    // state objects initialization
    const [ user,  setUserData ] = useState(null);

    useEffect(() => {

        const config = {
            params: {
                uniqueId: id
            }
        } 
        axios.get(`${process.env.REACT_APP_BASE_URL}/gather/core/anonymous/user/data`, config).then((res) => {
            if (res.data.message === "Successfully gathered core user information!") {
                console.log(res.data);

                const { user } = res.data;

                setUserData(user);
            } else {
                console.log("Err", res.data);
            }
        }).catch((err) => {
            console.log(err);
        })
    }, [])
    const [activeTab, setActiveTab] = useState('1');

    const lastProfileItem = (user !== null ? user.profilePicsVideos[user.profilePicsVideos.length - 1] : null);
    return (
        <Fragment>
            <Breadcrumb parent="Submitted Applications" title="Application/Applicant details & information" />
            <Container fluid={true}>
                <div className="user-profile social-app-profile">
                    <Row>
                        <Col sm="12">
                            <Card className="hovercard text-center add-shadow-md-custom">
                                <div className="cardheader socialheader">
                                    <img src={user !== null && _.has(user, "profileBannerImage") ? `${process.env.REACT_APP_ASSET_LINK}/${user.profileBannerImage.link}` : require("../../../../../../../assets/images/placeholder.png")} className="stretch-and-cover-image" />
                                </div>
                                <div className="user-image">
                                    <div className="avatar">
                                        {user !== null ? renderPictureOrVideoProfilePic(lastProfileItem) : <Media body alt="user" src={require("../../../../../../../assets/images/placeholder.png")} />}
                                    </div>
                                    <ul className="share-icons">
                                        <li><a className="social-icon bg-primary" href={null}><i className="fa fa-smile-o"></i></a></li>
                                        <li><a className="social-icon bg-secondary" href={null}><i className="fa fa-wechat"></i></a></li>
                                        <li><a className="social-icon bg-warning" href={null}><i className="fa fa-share-alt"></i></a></li>
                                    </ul>
                                </div>
                                <div className="info market-tabs p-0">
                                    <Nav tabs className="border-tab tabs-scoial">
                                        <NavItem className="nav" id="myTab" role="tablist">
                                            <NavLink tag="a" href={null} className={activeTab === '1' ? 'active' : ''} onClick={() => setActiveTab('1')}>
                                                {Timline}
                                        </NavLink>
                                        </NavItem>
                                        <NavItem className="nav" id="myTab" role="tablist">
                                            <NavLink tag="a" href={null} className={activeTab === '2' ? 'active' : ''} onClick={() => setActiveTab('2')}>
                                                {About}
                                        </NavLink>
                                        </NavItem>
                                        <NavItem style={{ paddingTop: "60px" }}>
                                            <div className="user-designation"></div>
                                            <h5 style={{ textDecorationLine: "underline" }} className="text-center">Applicant Info (<strong style={{ color: "#a927f9", textDecorationLine: "underline" }}>Username & Name</strong>)</h5>
                                            <div className="title"><a target="_blank" href={null}>{user !== null ? user.username : "Loading/Unknown..."}</a></div>
                                            <div className="desc mt-2">{user !== null ? `${user.firstName} ${user.lastName}` : "Loading/Unknown..."}</div>
                                        </NavItem>
                                        <NavItem className="nav" id="myTab" role="tablist">
                                            <NavLink tag="a" href={null} className={activeTab === '3' ? 'active' : ''} onClick={() => setActiveTab('3')}>
                                                {Friends}
                                        </NavLink>
                                        </NavItem>
                                        <NavItem className="nav" id="myTab" role="tablist">
                                            <NavLink tag="a" href={null} className={activeTab === '4' ? 'active' : ''} onClick={() => setActiveTab('4')}>
                                                {Photos}
                                        </NavLink>
                                        </NavItem>
                                    </Nav>
                                </div>
                            </Card>
                        </Col>
                    </Row>
                    <TabContent activeTab={activeTab} className="tab-content">
                        <TabPane tabId="1">
                            <TimelineTab calculateFileType={calculateFileType} applicantData={applicantData} />
                        </TabPane>
                        <TabPane tabId="2">
                            <AboutTab applicantData={applicantData} />
                        </TabPane>
                        <TabPane tabId="3">
                            <FriendsTab applicantData={applicantData} />
                        </TabPane>
                        <TabPane tabId="4">
                            <PhotosTab applicantData={applicantData} />
                        </TabPane>
                    </TabContent>
                </div>
            </Container>
        </Fragment>
    );
};

export default withRouter(ManageApplicationIndividualHelper);
