import React, { Fragment } from "react";
import "./styles.css";
import { ListGroupItem } from 'reactstrap';


const ItemRenderer = ({ item, methods }) => {
    console.log("item", item);
    return (
        <Fragment key={item.id}>
            <ListGroupItem className="list-group-item-action flex-column align-items-start">
                <div className="d-flex w-100 justify-content-between">
                    <h5 className="mb-1">{item.name}</h5><small>{`Asset ID ~ ${item.id}`}</small>
                </div>
                <p className="mb-1">{`This 'asset' has multiple reward tier's with 'low severity' paying the least generally and 'critical' paying the most, the employer and you will determine this vulerability risk/rating or class..`}</p>
                <Row>
                    <Col sm="6" lg="3" md="3" xl="3">
                        <Badge color="primary">{item.lowSeverity}</Badge>
                    </Col>
                    <Col sm="6" lg="3" md="3" xl="3">
                        <Badge color="secondary">{item.mediumSeverity}</Badge>
                    </Col>
                    <Col sm="6" lg="3" md="3" xl="3">
                        <Badge color="info">{item.highSeverity}</Badge>
                    </Col>
                    <Col sm="6" lg="3" md="3" xl="3">
                        <Badge color="success">{item.criticalSeverity}</Badge>
                    </Col>
                </Row>
            </ListGroupItem>
            {/* <div className="custom-styled-dropdown-submit-work">
                <div onClick={() => methods.addItem(item)}>
                    <input onChange={() => methods.addItem(item)} type="checkbox" checked={methods.isSelected(item)} /> {item.label}
                </div>
            </div> */}
        </Fragment>
    );
};

export default {
    ItemRenderer
};
