import React, { Fragment } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { Row, Col, Form, Label, Input, ListGroupItem, ListGroup, Button } from 'reactstrap';
import { NotificationManager } from "react-notifications";

const PageTwoVideoCreationHelper = () => {

    const { register, handleSubmit, control, reset, getValues, setValue, setError, clearErrors, formState: { errors }} = useForm({
        mode: "onTouched",
        reValidateMode: "onBlur"
    });

    const gatheredValues = getValues();

    console.log("gatheredValues", gatheredValues);

    const onSubmit = data => {

        if (data !== '') {
            alert('You submitted the form and stuff!');
        } else {
            errors.showMessages();
        }
    };

    return (
        <Fragment>
            <Row>
                <Col sm="12">
                    <Form className="needs-validation" onSubmit={handleSubmit(onSubmit, (errorsss, e) => {
                        console.log("errrrrrs", errorsss, e);
                    })}>
                        <div className="form-row">

                        </div>
                    </Form>
                </Col>
            </Row>
        </Fragment>
    );
};

export default PageTwoVideoCreationHelper;
