import React, { useEffect, useState, useRef, Fragment } from 'react';
import { Col, Button, Form, Label, Input, Card, CardHeader, CardBody, Media, Row, Popover, PopoverBody, PopoverHeader } from 'reactstrap';
import "./styles.css";
import _ from "lodash";
import { useHistory } from "react-router-dom";
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton';
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { saveSoftwareListingInfo } from "../../../../../../../redux/actions/hackers/createSoftwareListing/createNewSoftwareListingSale.js";

const PageFourMainHelper = ({ previouslySavedSoftwareData }) => {
    // create history object to eventually redirect upon submission
    const history = useHistory();
    // state initialization
    const [ ready, setReadyState ] = useState(false);


    // mounted and prepared check
    useEffect(() => {

        console.log("previouslySavedSoftwareData", previouslySavedSoftwareData);

        if (previouslySavedSoftwareData) {
            setReadyState(true);   
        }
    }, [])
    // conditional render helper function (MAIN)...
    const renderMainContentChunk = () => {
        if (ready === true) {
            return (
                <Fragment>
                    <Media>
                        <img className="img-40 img-fluid m-r-20" src={require("../../../../../../../assets/images/user/3.png")} alt="" />
                        <Media body>
                            <h6 className="f-w-600">
                                <a href={null}>Listing Title: {previouslySavedSoftwareData.listingTitle}</a>
                                <span className="pull-right">
                                    <Link to={`/`}> 
                                        <Button color="primary">{"Edit this portion of data"}</Button>
                                    </Link>
                                </span>
                            </h6>
                                <p>Something
                                <span>
                                    <i className="fa fa-star font-warning"></i>
                                    <i className="fa fa-star font-warning"></i>
                                    <i className="fa fa-star font-warning"></i>
                                    <i className="fa fa-star font-warning"></i>
                                    <i className="fa fa-star font-warning"></i>
                                </span>
                            </p>
                        </Media>
                    </Media>
                    <div style={{ paddingTop: "17.5px" }} className="job-description">
                        <h6>Listing Title</h6>
                        <p>{"Creative is looking for a UI/UX Designer to join our team. The world is seeing an explosion in the amount and variety of location-baWe are looking for a versatile UX/UI Designer to join our growing design team. Our designers contribute to clients’ projects at all stages of development. We might start from scratch, conducting user and stakeholder interviews, making personas and journey maps, and continue on to iterating on designs and prototypes through delivering final assets for launch. We might come into the middle of an in-flight program-size project and conduct analysis and usability correction or feature enhancement. We might provide research and vision for handoff to an internal development team."}</p>
                    </div>
                </Fragment>
            );
        } else {
            // not ready - render skelaton
            return (
                <Fragment>
                    <SkeletonTheme baseColor="#d4d4d4" highlightColor="#8f8f8f">
                        <p>
                            <Skeleton containerClassName={"stretch-bars"} count={45} />
                        </p>
                    </SkeletonTheme>
                </Fragment>
            );
        }
    }

    return renderMainContentChunk();
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data,
        previouslySavedSoftwareData: state.softwareListingSale.softwareListingSaleInfo
    }
}
export default connect(mapStateToProps, { saveSoftwareListingInfo })(PageFourMainHelper);
