import React, { Fragment } from "react";
import ReactPlayer from "react-player";
import _ from "lodash";
import "./styles.css";
import ReactImageVideoLightbox from 'react-image-video-lightbox';

const renderPictureOrVideoLast = (file) => {
    if (file !== null && _.has(file, "link")) {
        if (file.type.includes("video")) {
            // video logic
            return (
                <Fragment>
                    <li>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} className={"img-fluid"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        } else {
            // image logic
            return (
                <Fragment>
                    <li>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <img className="img-fluid" alt="post1" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        }  
    } else {
        // image logic - DEFAULT.
        return (
            <Fragment>
                <li>
                    <div className="latest-post profile-pic-video-sidebar-custom">
                        <img className="img-fluid" alt="post1" src={process.env.REACT_APP_PLACEHOLDER_IMAGE} />
                    </div>
                </li>
            </Fragment>
        );
    } 
}
const RenderGalleryModalHackerProfileHelper = ({ onCloseModal, isOpen }) => {
    return (
        <Fragment>
            <ReactImageVideoLightbox
                data={[
                    {
                    url: "https://placekitten.com/450/300",
                    type: "photo",
                    altTag: "some image",
                    },
                    {
                    url: "https://www.youtube.com/embed/ScMzIvxBSi4",
                    type: "video",
                    title: "some video",
                    },
                    {
                    url: "https://placekitten.com/550/500",
                    type: "photo",
                    altTag: "some other image",
                    },
                    {
                    url: "https://www.youtube.com/embed/ScMzIvxBSi4",
                    type: "video",
                    title: "some other video",
                    },
                ]}
                startIndex={0}
                showResourceCount={true}
                onCloseCallback={this.callbackFunction}
                onNavigationCallback={(currentIndex) =>
                    console.log(`Current index: ${currentIndex}`)
                }
                />
        </Fragment>
    );
}
export default {
    renderPictureOrVideoLast,
    RenderGalleryModalHackerProfileHelper
};
