import React, { Fragment } from 'react';

const handleFileDeletionUploadedFiles = (i, setMyFile, setPopoverStates) => {
    setMyFile(prevState => {
        return prevState.filter((item, idx) => {
            if (item.id !== uploadedSelected.id) {
                return true;
            }
        })
    });
    setPopoverStates(prevState => {
        return {
            ...prevState,
            [`popover${i}`]: false
        }
    });
}

export default {
    handleFileDeletionUploadedFiles
}
