import React, { useState, useRef, useEffect } from 'react';
import "./styles.css";
import { connect } from "react-redux";
import { NotificationManager } from 'react-notifications';
import { saveSoftwareListingInfo } from "../../../../../../../redux/actions/hackers/createSoftwareListing/createNewSoftwareListingSale.js";
import _ from "lodash";
import { useHistory } from "react-router-dom";
import helpers from "./helpers/mainFunctions.js";
import { Col, Button, Form, Label, Input, Card, CardHeader, CardBody, Media, Row, Popover, PopoverBody, PopoverHeader, Container } from 'reactstrap';
import { useForm, Controller } from 'react-hook-form';

const PageThreeHelper = ({ saveSoftwareListingInfo, previouslySavedSoftwareData, userData }) => {
    // create redirect ability
    const history = useHistory();

    const { register, handleSubmit, control, reset, setValue, setError, clearErrors, formState: { errors }} = useForm({
        mode: "onTouched",
        reValidateMode: "onBlur"
    });

    // react state initialization goes here...
    const [ data, setData ] = useState({
        demoURL: ""
    });
    // const [validateClass , setValidateClass] = useState(false);

    const handleInputChange = (e) => {
        const { name, value } = e.target;

        clearErrors(name);

        setData(prevState => {
            return {
                ...prevState,
                [name]: value
            }
        });

        return value;
    };
    const onSubmit = (data, e) => {
        // prevent reload
        e.preventDefault();

        if (data !== '') {
            // everything is properly filled out
            console.log("success! :) - : ", data);
        } else {
            errors.showMessages();
        }
    };
    const onError = (errors, e) => {
        console.log("error submitting...!", errors);
    };
    return (
        <div>
            <Container fluid={true}>
                <Form className={`needs-validation tooltip-validation ${true ? 'validateClass' : ''}`} noValidate="" onSubmit={handleSubmit(onSubmit, onError)}>
                    <Row>
                        <Col sm="12" md="6" lg="6" xl="6">
                            <Label>Demo URL (optional - if you want to display working code at an external URL)</Label>
                            <input {...register("demoURL", { required: false, minLength: {
                                value: 15,
                                message: "You must enter AT Least 15 characters"
                            }, maxLength: {
                                value: 75,
                                message: "You may ONLY enter 75 characters or less"
                            }, onBlur: (value) => {
                               return helpers.CalculateWhetherURLIsLegit(value); 
                            }})} className="form-control" name="demoURL" type="text" placeholder="Listing title" onChange={handleInputChange} value={data.demoURL} />
                            <div className="valid-feedback">{"Looks good!"}</div>
                        </Col>
                        <Col sm="12" md="6" lg="6" xl="6">
                            <Label>Something else will end up here :)</Label>
                            {/* <input {...register("demoURL", { required: false, minLength: {
                                value: 15,
                                message: "You must enter AT Least 15 characters"
                            }, maxLength: {
                                value: 75,
                                message: "You may ONLY enter 75 characters or less"
                            }})} className="form-control" name="demoURL" type="text" placeholder="Listing title" onChange={handleInputChange} value={data.demoURL} /> */}
                            <div className="valid-feedback">{"Looks good!"}</div>
                        </Col>
                    </Row>
                    <Row style={{ marginTop: "45px" }}>
                        <Col sm="12" md="12" lg="12" xl="12">
                            <Button color="secondary" style={{ width: "100%" }} type="submit" onClick={() => {

                            }}>~ Submit form details and proceed to next page ~</Button>
                        </Col>
                    </Row>
                </Form>
            </Container>
        </div>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data,
        previouslySavedSoftwareData: state.softwareListingSale.softwareListingSaleInfo
    }
}
export default connect(mapStateToProps, { saveSoftwareListingInfo })(PageThreeHelper);
