import React, { Fragment } from "react";
import "./styles.css";
import { connect } from "react-redux";
import helpers from "./helpers/helperFunctions/helpers.js";
import { Button, Row, Col, Card, CardBody, CardHeader, FormGroup, Label, Input, Media, InputGroup, Form, InputGroupAddon, InputGroupText, Progress, ListGroupItem, Container } from 'reactstrap';
import { updateCourseInformationData } from "../../../../../../../../redux/actions/courses/createNewCourse/index.js";
import _ from "lodash";
import uuid from "react-uuid";
import moment from "moment";
import { useForm, Controller } from 'react-hook-form';
import reactHookHelpers from "./helpers/helperFunctions/hookFormHelpers.js";

// react-hook-form imports/helpers
const subtitleChecks = reactHookHelpers().subtitleChecks;


// actual component
const CreateNewCoursePageThree = ({ userData, courseData, overallProgress, setOverallProgress, setProgress }) => {

    // redux form logic
    const { register, handleSubmit, control, resetField, unregister, getValues, array, setValue, setError, clearErrors, formState: { errors }} = useForm({
        mode: "onTouched",
        reValidateMode: "onBlur"
    });

    const currentValues = getValues();

    const handleSubmission = () => {
        console.log("handleSubmission ran...");
    }
    const onSubmit = (data, e) => {
        console.log("onSubmit ran...", data, e);
    }
    const errorsSubmit = (e, errors) => {
        console.log("errorsSubmit", e, errors);
    }
    return (
        <Fragment>
            <div className={"centered-horizontally-course"}>
                <div className={"position-above-bar-percentage"}>
                    <h1>{overallProgress}% Complete</h1>
                </div>
                <Progress className={"course-creation-progress-bar"} animated color="info" value={overallProgress} />
            </div>
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                            <Card>
                                <CardBody>
                                    <Row>
                                        <Col sm="12" md="12" lg="12" xl="12">
                                            <h4 className={"course-custom-title"}>More fine-grain specific details about your listing. Please fill out each of the following fields to the best of your ability & be as specific/detailed as possible. We will collect things such as difficulty level, language, etc...</h4>
                                        </Col>
                                    </Row>
                                    <Row style={{ marginTop: "17.5px" }}>
                                        <Card className={"specifics-card"}>
                                            <CardHeader className="b-l-primary card-header-customized-specifics">
                                                <h4>More required detailed information related to core listing</h4>
                                            </CardHeader>
                                            <CardBody>
                                                <Form onSubmit={handleSubmit(onSubmit, (e, errors) => {
                                                    return errorsSubmit(e, errors);
                                                })}>
                                                    <div className="form-row">
                                                        <Col sm="12 mb-3" lg="12 mb-3" xl="12 mb-3" md="12 mb-3">
                                                            <Label>{subtitleChecks.label}</Label>
                                                            <Input {...subtitleChecks.check(setError, register)} onChange={(e) => subtitleChecks.onChange(e, setValue)} value={currentValues.subtitle} className="form-control" name={subtitleChecks.name} type={subtitleChecks.type} placeholder={subtitleChecks.placeholder} />
                                                            {errors.subtitle ? <span className="span-tooltip">{errors.subtitle.message}</span> : null}
                                                        </Col>
                                                    </div>
                                                    <div className="form-row">
                                                        <Col md="4 mb-3">
                                                            <Label>FirstName</Label>
                                                            <Input className="form-control" name="firstName" type="text" placeholder="First name" />
                                                        </Col>
                                                        <Col md="4 mb-3">
                                                            <Label htmlFor="validationCustom02">LastName</Label>
                                                            <Input className="form-control" name="lastName" type="text" placeholder="Last name" />
                                                        </Col>
                                                        <Col md="4 mb-3">
                                                        <Label htmlFor="validationCustomUsername">Username</Label>
                                                            <InputGroup>
                                                                <InputGroupAddon addonType="prepend">
                                                                    <InputGroupText>{"@"}</InputGroupText>
                                                                </InputGroupAddon>
                                                                <Input className="form-control" name="userName" type="text" placeholder="Username" />
                                                                <span>{errors.lastName && 'User name is required'}</span>
                                                            </InputGroup>
                                                        </Col>
                                                    </div>
                                                    <div className="form-row">
                                                        <Col md="6 mb-3">
                                                            <Label htmlFor="validationCustom03">City</Label>
                                                            <Input className="form-control" name="city" type="text" placeholder="City" />
                                                        </Col>
                                                        <Col md="3 mb-3">
                                                            <Label htmlFor="validationCustom04">State</Label>
                                                            <Input className="form-control" id="validationCustom04" name="state" type="text" placeholder="State" />
                                                        </Col>
                                                        <Col md="3 mb-3">
                                                            <Label htmlFor="validationCustom05">Zip</Label>
                                                            <Input className="form-control" id="validationCustom05" name="zip" type="text" placeholder="Zip" />
                                                        </Col>
                                                    </div>
                                                    <hr />
                                                    <Button style={{ width: "100%" }} outline className={"btn-square-info"} color="info-2x">{"Submit form & proceed w/rest of logic"}</Button>
                                                    </Form>
                                            </CardBody>
                                        </Card>
                                    </Row>
                                </CardBody>
                            </Card>
                        </Col>
                    </Row>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card>
                            <CardBody>

                            </CardBody>
                        </Card>
                    </Col>
                </Row>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card>
                            <CardBody>
                                <Button onClick={handleSubmission} className={"btn-square-secondary"} color={"secondary-2x"} outline style={{ width: "100%" }}>Submit & Continue W/Form Process</Button>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data,
        courseData: _.has(state.courseData, "courseData") ? state.courseData.courseData : []    
    }
}
export default connect(mapStateToProps, { updateCourseInformationData })(CreateNewCoursePageThree);
