import React from "react";
import { NotificationManager } from 'react-notifications';


// functions with export ability to slim down main component (pageThreeMain.js)..
export const calculateWhetherURLIsLegit = (data) => {
    console.log("data calculateWhetherURLIsLegit : ", data);

    const isValidHttpUrl = (string) => {
        let url;

        try {
          url = new URL(string);
        } catch (_) {
          return false;  
        }

        return url.protocol === "http:" || url.protocol === "https:";
    }
    if (isValidHttpUrl(data)) {
        return true;
    } else {
        return "You *must* enter a valid URL consisting of either https:// OR http:// along with .net/com/etc...";
    };
}
//
