import React, { Fragment, useEffect, useState } from 'react';
import "./styles.css";
import { Card, CardBody, CardHeader, Col, Row, Container } from "reactstrap";
import Breadcrumb from '../../../../../../../layout/breadcrumb';
import { Review, AlertOctagon, MessageCircle, UserPlus } from 'react-feather';
import CountUp from 'react-countup';
import { useParams } from 'react-router-dom';
import { connect } from "react-redux";
import axios from "axios";

const ManageIndividualHackerAlreadyHiredHelper = ({ userData }) => {

    const [ currentReviews, setCurrentReviewsLength ] = useState(0);
    const [ currentHireLength, setCurrentHireLength ] = useState(0);

    const { id } = useParams();

    console.log("ID from params...:", id);

    useEffect(() => {
        const config = {
            params: {
                relevantHackerID: id,
                employerID: userData.uniqueId
            }
        }
        axios.get(`${process.env.REACT_APP_BASE_URL}/gather/core/user/information/related/hired/hackers`, config).then((res) => {
            if (res.data.message === "Successfully gathered appropriate info!") {
                console.log(res.data);

                const { currentHires, reviews, currentApplication } = res.data;

                setCurrentReviewsLength(reviews);
                setCurrentHireLength(currentHires);
            } else {
                console.log("Err", res.data);
            }
        }).catch((err) => {
            console.log("Critical err", err);
        })
    }, [])
    return (
        <Fragment>
            <Breadcrumb parent="Manage Live HIRED hacker" title="Manage payments, contract conditions, work submissions & more..."/>
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className={"card-manage-hacker-margin"}>
                            <CardHeader className="b-l-primary border-3">
                                <h5>Manage this applicant from payments to reviews & work submissions & more...</h5>
                            </CardHeader>
                            <CardBody>
                                <Row>
                                    <Col sm="6" xl="3" lg="3">
                                        <Card className="o-hidden">
                                            <CardBody className="bg-primary b-r-4 card-body">
                                                <div className="media static-top-widget">
                                                <div className="align-self-center text-center"><Review /></div>
                                                    <div className="media-body"><span className="m-0"># of current reviews and/or completed gigs</span>
                                                        <h4 className="mb-0 counter"><CountUp duration={10.75} end={currentReviews} /></h4><Review className="icon-bg" />
                                                    </div>
                                                </div>
                                            </CardBody>
                                        </Card>
                                    </Col>
                                    <Col sm="6" xl="3" lg="3">
                                        <Card className="o-hidden">
                                            <div className="bg-secondary b-r-4 card-body">
                                                <div className="media static-top-widget">
                                                <div className="align-self-center text-center"><AlertOctagon /></div>
                                                    <div className="media-body"><span className="m-0"># of <strong>current</strong> and/or active hire's</span>
                                                        <h4 className="mb-0 counter"><CountUp duration={10.75} end={currentHireLength} /></h4><AlertOctagon className="icon-bg" />
                                                    </div>
                                                </div>
                                            </div>
                                        </Card>
                                    </Col>
                                    <Col sm="6" xl="3" lg="3">
                                        <Card className="o-hidden">
                                            <CardBody className="bg-primary b-r-4">
                                                <div className="media static-top-widget">
                                                <div className="align-self-center text-center"><MessageCircle /></div>
                                                    <div className="media-body"><span className="m-0">Messages</span>
                                                        <h4 className="mb-0 counter"><CountUp duration={10.75} end={893} /></h4><MessageCircle className="icon-bg" />
                                                    </div>
                                                </div>
                                            </CardBody>
                                        </Card>
                                    </Col>
                                    <Col sm="6" xl="3" lg="3">
                                        <Card className="o-hidden">
                                            <CardBody className="bg-info b-r-4">
                                                <div className="media static-top-widget">
                                                <div className="align-self-center text-center"><MessageCircle /></div>
                                                    <div className="media-body"><span className="m-0">Messages</span>
                                                        <h4 className="mb-0 counter"><CountUp duration={10.75} end={893} /></h4><MessageCircle className="icon-bg" />
                                                    </div>
                                                </div>
                                            </CardBody>
                                        </Card>
                                    </Col>
                                </Row>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
                <Row>
                    <Col sm="12" md="5" lg="5" xl="5">
                        <Card>
                            <CardBody>

                            </CardBody>
                        </Card>
                    </Col>
                    <Col sm="12" md="7" lg="7" xl="7">
                        <Card>
                            <CardBody>

                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, { })(ManageIndividualHackerAlreadyHiredHelper);
