import React, { Fragment } from 'react';
import { Card, CardBody, CardHeader, Col, Container, Row } from 'reactstrap';
import "./styles.css";
import Breadcrumb from '../../../../../../../layout/breadcrumb'
import Select from 'react-dropdown-select';
import helpers from "./helpers/miscFunctions.js";


const { ItemRenderer } = helpers;


const options = {
    label: "one", value: "one",
    label: "one", value: "one",
    label: "one", value: "one",
    label: "one", value: "one",
    label: "one", value: "one",
    label: "one", value: "one"
}

const SubmitProgressUpdatesForEmployerHelper = (props) => {
    const companyName = "EXNESS";

    const handleAssetValueChange = (values) => {
        console.log("handleAssetValueChange values...:", values);
    }
    return (
        <Fragment>
            <Breadcrumb parent="Manage This Active Job & Submit A Report!" title="Submit A Report Or Follow Up On A Previous Report.." />
            <Container fluid={true}>
                <Row>
                    <Col sm="12" lg="12" md="12" xl="12">
                        <Card className='shadow'>
                            <CardHeader className='b-l-info b-r-info'>
                                <h3 className='header-submit-report'>You're about to submit a report to {companyName}</h3>
                            </CardHeader>
                            <CardBody>
                                <p className='lead'>You're about to submit a report to {companyName}. Provide as much information as possible about the potential issue you have discovered. The more information you provide, the quicker EXNESS will be able to validate the issue. If you haven't yet, please remember to review our Policy and Disclosure Guidelines.</p>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
                <Row>
                    <Col sm="12" lg="12" md="12" xl="12">
                        <Card className='shadow'>
                            <CardHeader className='b-l-info b-r-info'>
                                <h3 className='header-submit-report'>Asset(s)</h3>
                                <p className='lead'>Select the attack surface of this issue.</p>
                            </CardHeader>
                            <CardBody>
                                <Select
                                    multi={false}
                                    options={options}
                                    onChange={(values) => handleAssetValueChange(values)}
                                    itemRenderer={ItemRenderer}
                                />
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}

export default SubmitProgressUpdatesForEmployerHelper;
