import React, { Fragment, useState } from "react";
import Sheet from 'react-modal-sheet';
import { Button, Container, Col, Row, Card, FormGroup, Label, CardBody, Form } from "reactstrap";
import "./styles.css";
import Breadcrumb from "../../../../../../../../../layout/breadcrumb";
import { Link } from "react-router-dom";

const SlideUpPaneAuctionPurchaseSelectionData = ({ selected }) => {

    console.log("typeOfListing", selected);

    const [ isOpen, setModalOpenStatus ] = useState(false);

    const renderConditionalBasedUponSellingType = (type) => {
        switch (type) {
            case "auction-ONLY":
                return (
                    <Fragment>
                        <CardBody>
                            <Form className="theme-form" onSubmit={null}>
                                <Row>
                                    <Col>
                                    <FormGroup>
                                        <Label>Title</Label>
                                        <input className="form-control" type="text"  name="title" placeholder="Project name *" />
                                        {null ? <span className="span-tooltip">{""}</span> : null}
                                    </FormGroup>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col>
                                        <FormGroup className="mb-0">
                                            <Button color="success" className="mr-3">Submit?</Button>
                                                <Link to={`/`}>
                                                    <Button color="danger">Cancel</Button>
                                                </Link>
                                        </FormGroup>
                                    </Col>
                                </Row>
                            </Form>
                        </CardBody>
                    </Fragment>
                );
                break;
            case "auction-AND-buy-it-now":
                return (
                    <Fragment>
                        <h1>{type}</h1>
                    </Fragment>
                );
                break;
            case "buy-it-now-OR-best-offer":
                return (
                    <Fragment>
                        <h1>{type}</h1>
                    </Fragment>
                );
                break;
            case "buy-it-now-ONLY":
                return (
                    <Fragment>
                        <h1>{type}</h1>
                    </Fragment>
                );
                break;
            default:
                break;
        }
    }
    return (
        <Fragment>
            <div style={{ paddingTop: "27.5px", paddingBottom: "12.5px" }} className="centered-both-ways">
                <Button onClick={() => {
                    setModalOpenStatus(true);
                    // open modal to modify existing photos/images
                }} outline className="btn-pill btn-air-success stretched-purchase-options-btn" color="success" size="lg">Edit/Modify Existing Image/Photo's</Button>
            </div>
            <Sheet isOpen={isOpen} onClose={() => setModalOpenStatus(false)}>
                <Sheet.Container>
                <Sheet.Header>

                </Sheet.Header>
                <Sheet.Content> 
                    <div className="add-normal-backer">
                        <Breadcrumb passedClassname={"custom-breadcrumb-class"} parent="Auction purchase options/settings" title="Select your preferred auction type"/>
                        <Container fluid={true}>
                            <Card className="card-main-container">
                                <Row>
                                    <Col sm="12" md="12" lg="12" xl="12">
                                        {renderConditionalBasedUponSellingType(selected)}
                                    </Col>
                                </Row>
                            </Card>
                        </Container>
                    </div>
                </Sheet.Content>
                </Sheet.Container>

                <Sheet.Backdrop />
            </Sheet>
        </Fragment>
    );
}
export default SlideUpPaneAuctionPurchaseSelectionData;
