                                                <div className="product-img">
                                                    <Media className="img-fluid top-radius-blog" src={require("../../../../assets/images/faq/1.jpg")} alt="" />
                                                    <div className="product-hover">
                                                        <ul>
                                                            <li><i className="icon-link" onClick={() => clickApply()}></i></li>
