import React, { Fragment, useState, useRef } from "react";
import "./styles.css";
import { Button, Row, Col, Card, CardBody, CardHeader, Progress, Container } from 'reactstrap';
import { connect } from "react-redux";
import _ from "lodash";
import ReactPlayer from "react-player";
import Sheet from 'react-modal-sheet';


const ViewCourseDataPaneHelper = ({ isOpen, setIsOpenState, selectedItem }) => {

    console.log("videoUrl selectedItem", selectedItem);

    const videoUrl = `${process.env.REACT_APP_ASSET_LINK}/${selectedItem.video.link}`;
    return (
        <Fragment>
            <Sheet isOpen={isOpen} onClose={() => setIsOpenState(false)}>
                <Sheet.Container>
                <Sheet.Header />
                <Sheet.Content>
                    <Container style={{ marginTop: "25px" }} fluid={true}>
                        <Row>
                            <Col sm="12" md="12" lg="12" xl="12">
                                <Card>
                                    <CardBody>
                                        <Col sm="12" md="6" lg="6" xl="6">
                                            <Card className="height-equal">
                                                <div className="calender-widget">
                                                    <div className="cal-img">
                                                        <ReactPlayer controls={true} playIcon={true} playing={false} loop={false} volume={1} width={"100%"} height={"100%"} url={videoUrl} className={"override-widget-picture-course"} />
                                                    </div>
                                                    <div className="cal-date">

                                                    </div>
                                                    <div className="cal-desc text-center card-body">
                                                        <h6 className="f-w-600">{"Above is your 'Demo/Promo Video'..."}</h6>
                                                        <p className="text-muted mt-3 mb-0">{"This video will be used to give people a general idea of whta they're getting themselves into when purchasing your course & more of an idea of what the content consists of."}</p>
                                                    </div>
                                                </div>
                                            </Card>
                                        </Col>
                                        <Col sm="12" md="6" lg="6" xl="6">

                                        </Col>
                                    </CardBody>
                                </Card>
                            </Col>
                        </Row>
                    </Container>
                </Sheet.Content>
                </Sheet.Container>

                <Sheet.Backdrop />
            </Sheet>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data,
        courseData: _.has(state.courseData, "courseData") ? state.courseData.courseData : null
    }
}
export default connect(mapStateToProps, { })(ViewCourseDataPaneHelper);
