import React from 'react';
import "./styles.css";
import Sheet from 'react-modal-sheet';
import { Button, Container, Row, Col, Card, CardHeader, CardBody } from "reactstrap";
import axios from 'axios';

const BoostAlreadyPostedEmployerListingPaneHelper = ({ authData, listingBoostSelected, employerListingPromotePane, setSheetOpenEmployerListingPromote }) => {

    useEffect(() => {

        const configuration = {
            params: {
                id: authData.uniqueId
            }
        }
        axios.get(`${process.env.REACT_APP_BASE_URL}/fetch/related/employer/listings/employer/account`, configuration).then((res) => {
            if (res.data.message === "Gathered related listings to choose from!") {
                console.log(res.data);

                const { listings } = res.data;
            } else {
                console.log("Err", res.data);
            }
        }).catch((err) => {
            console.log(err);
        })
    }, [])

    const calculateTier = (tier) => {
        switch (tier) {
            case "tier-1":
                return "Tier One (1)";
                break;
            case "tier-2":
                return "Tier Two (2)";
                break;
            case "tier-3":
                return "Tier Three (3)";
                break;
            default:
                break;
        }
    }
    return (
        <div>
            <Sheet draggable={false} isOpen={employerListingPromotePane} onClose={() => setSheetOpenEmployerListingPromote(false)}>
                <Sheet.Container>
                <Sheet.Header>
                    <div className='margin-medium-button-pane'>
                        <Button className={"btn-square-danger"} color={"danger-2x"} outline style={{ width: "100%" }} onClick={() => setSheetOpenEmployerListingPromote(false)}>Close/Exit Pane</Button>
                    </div>
                </Sheet.Header>
                <Sheet.Content>
                    <Container fluid={true}>
                        <Row style={{ marginTop: "25px" }}>
                            <Col sm="12" md="12" lg="12" xl="12">
                                <Card className='bordered-shadowed-card-deposit'>
                                    <CardHeader className={"b-l-info b-r-info"}>
                                        <h2 className='header-deposit-card-funds'>You've selected {listingBoostSelected} which means you have selected the <strong style={{ color: "#f73164", textDecorationLine: "underline" }}>{calculateTier(listingBoostSelected)}</strong> boost 'tier' which cooresponding between 1 and 3 (3 being the most intensive 'boost')</h2>
                                        <p className='lead'>Please select the <strong style={{ color: "#f73164" }}>previously posted employer listing</strong> you'd like to promote and select your payment method (if applicable - you may need to add a payment method). We will display your previously posted <strong style={{ color: "#f73164" }}>ACTIVE</strong> contracts to allow you to choose from..</p>
                                    </CardHeader>
                                    <CardBody>

                                    </CardBody>
                                </Card>
                            </Col>
                        </Row>
                        <Row>
                            <Col sm="12" md="12" lg="12" xl="12">
                                <Card>
                                    <CardBody className='bordered-shadowed-card-deposit'>
                                        <h2 style={{ textDecorationLine: "underline" }} className='header-deposit-card-funds'>Please select the payment method you'd like to use for this transaction..</h2>
                                    </CardBody>
                                </Card>
                            </Col>
                        </Row>
                    </Container>
                </Sheet.Content>
                </Sheet.Container>
                <Sheet.Backdrop />
            </Sheet>
        </div>
    );
}

export default BoostAlreadyPostedEmployerListingPaneHelper;
