import React, { Fragment, useEffect, useState } from 'react';
import Sheet from 'react-modal-sheet';
import { Container, Row, Col, Card, CardHeader, CardBody, Button, Form, FormGroup, Label, Input, InputGroup, InputGroupAddon, InputGroupText } from 'reactstrap';
import { useForm } from 'react-hook-form';
import "./styles.css";
import uuid from "react-uuid";

const MessagingPanePrivateHelper = ({ messagePaneOpen, setMessagePaneState, employerName }) => {
    console.log("messagePaneOpen", messagePaneOpen);

    const { register, handleSubmit, errors } = useForm();

    const uniqueChatID = uuid();

    useEffect(() => {

        // sb.OpenChannel.createChannel((openChannel, error) => {
        //     if (error) {
        //         // Handle error.
        //         console.log("ERROR OPENING CHANNEL! - : ", error);
        //     }
        //     console.log("SUCCESSFULLY OPENED CHANNEL! - : ", openChannel);
        //     // An open channel is successfully created.
        //     // Through the "openChannel" parameter of the callback function,
        //     // you can get the open channel's data from the result object that Sendbird server has passed to the callback function.
        // });
    }, [])

    const onSubmit = (e, data) => {
        console.log("onSubmit", e, data);
    }
    const renderFormError = (e, errors) => {
        console.log("renderFormError ran...", e, errors);
    }
    return (
        <Fragment>
            <Sheet isOpen={messagePaneOpen} onClose={() => setMessagePaneState(false)}>
                <Sheet.Container>
                <Sheet.Header />
                <Sheet.Content>
                    <Container fluid={true}>
                        <Col lg="12 box-col-12" xl="12 xl-100" md="12">
                            <Card className={"card-shadow-messaging-initialize"}>
                                <CardBody>
                                    <h3>Create a new <strong>private message</strong> to {employerName}</h3>
                                    <p className="lead">This will send a "Private Message" to <strong style={{ textDecorationLine: "underline" }}>ONLY</strong> the employer/poster of this listing - this information is encrypted & protected for privacy.</p>
                                    <hr />
                                    <Form className="needs-validation" noValidate="" onSubmit={handleSubmit(onSubmit, (e, errrors) => renderFormError(e, errors))}>
                                            <div className="form-row">
                                                <Col md="6 mb-3">
                                                    <Label htmlFor="validationCustom01">Message Title/Subject</Label>
                                                    <InputGroup>
                                                        <InputGroupAddon addonType="prepend">
                                                            <InputGroupText>{"Msg Subject"}</InputGroupText>
                                                        </InputGroupAddon>
                                                        <Input className="form-control" name="userName" type="text" placeholder="Subject or title..." />
                                                    </InputGroup>
                                                </Col>
                                                <Col md="6 mb-3">
                                                    <Label htmlFor="validationCustom02">Something</Label>
                                                    <Input className="form-control" name="lastName" type="text" placeholder="Last name" />
                                                </Col>
                                            </div>
                                            <div className="form-row">
                                                <Col md="6 mb-3">
                                                    <Label htmlFor="validationCustom03">Something</Label>
                                                    <Input className="form-control" name="city" type="text" placeholder="City" />
                                                </Col>
                                                <Col md="3 mb-3">
                                                    <Label htmlFor="validationCustom04">Something</Label>
                                                    <Input className="form-control" id="validationCustom04" name="state" type="text" placeholder="State" />
                                                </Col>
                                                <Col md="3 mb-3">
                                                <Label htmlFor="validationCustom05">Zip</Label>
                                                <Input className="form-control" id="validationCustom05" name="zip" type="text" placeholder="Zip" />
                                                </Col>
                                            </div>
                                            <Button color="primary">{"Submit form"}</Button>
                                        </Form>
                                </CardBody>
                            </Card>
                        </Col>
                    </Container>
                </Sheet.Content>
                </Sheet.Container>

                <Sheet.Backdrop />
            </Sheet>
        </Fragment>
    );
}
export default MessagingPanePrivateHelper;
