import React, { Fragment } from 'react';
import Breadcrumb from '../../../../../../../../layout/breadcrumb'
import { Container, Row, Col, Card, CardHeader, CardBody, Form } from 'reactstrap'
import Dropzone from 'react-dropzone-uploader';
import { ToastContainer, toast } from 'react-toastify';
import {SingleFileUpload,MultiImageUpload,MultipleImageVideoAudioUpload,LimitationFileUpload,CustomFileUpload} from '../../../../../../../../constant'


const SlideOutPaneContent = (props) => {
    const getUploadParams = ({ meta }) => { return { url: 'https://httpbin.org/post' } };

    const handleChangeStatus = ({ meta, file }, status) => {
    }
    const handleSubmit = (files, allFiles) => {
        allFiles.forEach(f => f.remove())
        toast.success("Dropzone successfully submitted !");
    }
    return (
        <Fragment>
            <Container fluid={true}>
                <Row>
                    <Col sm="12" lg="12" md="12">
                        <h1 className="text-left">Upload a short video snippet or profile picture with the uploader.</h1>
                    </Col>
                </Row>
                <Row>
                    <Col sm="12">
                        <Card>
                            <CardHeader>
                                <h5>{SingleFileUpload}</h5>
                            </CardHeader>
                            <CardBody>
                                <Form>
                                    <div className="dz-message needsclick">
                                        <Dropzone
                                            getUploadParams={getUploadParams}
                                            onChangeStatus={handleChangeStatus}
                                            maxFiles={1}
                                            multiple={false}
                                            canCancel={false}
                                            inputContent="Drop A File"
                                            styles={{
                                                dropzone: { height: 200 },
                                                dropzoneActive: { borderColor: 'green' },
                                            }}
                                        />
                                    </div>
                                </Form>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}
export default SlideOutPaneContent;
