                                        </Row>
                                        <Row>
                                            <Col sm="12" md="6" lg="6" xl="6">
                                                <div className={"add-video-overlay-courses-upload centered-both-ways"}>
                                                    {currentFileSelectedUpload === null ? <div className={"overlay-boxshadow-courses centered-both-ways"}><h6 className={"header-no-content"}>Current NO file(s) is selected/uploaded - please select a file.</h6></div> : <ReactPlayer controls={true} playIcon={true} playing={false} loop={false} volume={1} width={"100%"} height={"100%"} url={filePathData} />}
                                                </div>
                                            </Col>
                                            <Col sm="12" md="6" lg="6" xl="6">
                                                <Dropzone
                                                    ref={dropzoneRef}
                                                    PreviewComponent={(data) => renderPreviewOfFile(data)}
                                                    maxFiles={1}
                                                    autoUpload={true}
                                                    onChangeStatus={(functions) => {
                                                        setChangeOptions(functions);
                                                    }}
                                                    submitButtonDisabled={false}
                                                    InputComponent={CustomInputHelper}
                                                    getFilesFromEvent={null}
                                                    onSubmit={onSubmitHelper}
                                                    accept="video/*"
                                                    SubmitButtonComponent={(data) => {
                                                        return (
                                                            <div className="absolutely-position-submit-btn">
                                                                {renderCustomButtonDropzone(data, setMetaFileData, currentFileSelectedUpload, fileMetaData, fileReady, setFileReadyStatus, setProgress, setVideoFileState, setShowInitialState)}
                                                            </div>
                                                        );
                                                    }}
                                                    className={"shadow-courses-dropzone"}
                                                    multiple={false}
                                                    canCancel={false}
                                                    styles={{
                                                        dropzone: { height: 350 },
                                                        dropzoneActive: { borderColor: 'green' },
                                                    }}
                                                />
                                                {fileReady === true ? <Fragment>
                                                    <hr className="secondary-hr" />
                                                    <div className="centered-both-ways">
                                                        <Button style={{ marginTop: "25px" }} onClick={(e) => {
                                                            setMetaFileData(null);
                                                            setFileReadyStatus(false);
                                                            setCurrentUploadFileStatus(null);
                                                            setCurrentFilePathData(null);

                                                            changeOptions.remove();
