                              <InputGroup className="bootstrap-touchspin">
                                <InputGroupAddon addonType="prepend">
                                  <Button color="primary btn-square" className="bootstrap-touchspin-down" onClick={minusQty}><i className="fa fa-minus"></i></Button>
