import React, { Fragment, useState, useEffect } from 'react';
import { Container, Row, Col, Card, CardHeader, CardBody, ListGroup, ListGroupItem } from "reactstrap";
import Breadcrumb from '../../../../../../layout/breadcrumb';
import axios from "axios";
import { NotificationManager } from 'react-notifications';
import { connect } from "react-redux";
import { useParams } from "react-router-dom";

const PurchaseCourseContentStripePaymentHelper = ({ userData }) => {

    const { id } = useParams();

    const [ cards, setCardData ] = useState([]);
    const [ viewList, setTotalViewsList ] = useState([]);
    const [ likes, setLikes ] = useState(0);
    const [ dislikes, setDislikes ] = useState(0);
    const [ activeTab, setActiveTabState ] = useState("1");

    useEffect(() => {
        const configuration = {
            params: {
                id: userData.uniqueId,
                accountType: userData.accountType
            }
        }
        axios.get(`${process.env.REACT_APP_BASE_URL}/gather/cards/payment/methods/both/accounts`, configuration).then((res) => {
            if (res.data.message === "Gathered employer payment cards!") {

                const { cards } = res.data;

                setCardData(cards);
            } else {
                console.log("err gathering card data...", res.data);

                NotificationManager.error("An error occurred while attempting to fetch the desired account 'card data', please reload the page or contact support if the problem persists!", "Error gathering account card data!", 4750);
            }
        }).catch((err) => {
            console.log(err);

            NotificationManager.error("An error occurred while attempting to fetch the desired account 'card data', please reload the page or contact support if the problem persists!", "Error gathering account card data!", 4750);
        });
    }, []);

    useEffect(() => {
        const configuration = {
            params: {
                id,
                signedInUserID: userData.uniqueId,
                fullName: `${userData.firstName} ${userData.lastName}`
            }
        }
        axios.get(`${process.env.REACT_APP_BASE_URL}/gather/individual/course/data`, configuration).then((res) => {
            if (res.data.message === "Successfully fetched course/listing!") {
                console.log(res.data);
                // successful request
                const { course } = res.data;
                // update course view count details
                setTotalViewsList(course.viewedByList);
                // set likes
                setLikes(course.likes);
                // set dislikes
                setDislikes(course.dislikes);
            } else {
                console.log("err", res.data);
            }
        }).catch((err) => {
            console.log(err);
        });
    }, [])

    console.log("Cards", cards);

    return (
        <Fragment>
            <Breadcrumb parent="Purchase A Educational/Instructional Course!" title="You're About To Purchase This Course.."/>
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className={"shadow"}>
                            <CardHeader className={"b-l-primary b-r-primary"}>
                                <h3>You're about to purchase this course, if you're sure about this decision - continue with the logic/data below & proceed with your purchase!</h3>
                            </CardHeader>
                            <CardBody>
                                <Row>
                                    <Col lg="6" sm="12" md="6" xl="6">
                                        <Card className='shadow'>
                                            <CardHeader>
                                                <div className="product-page-details">
                                                    <h3>{"Previously Added Card(s) & Method's"}</h3>
                                                </div>
                                            </CardHeader>
                                            <CardBody>
                                                <ListGroup>
                                                    {typeof cards !== "undefined" && cards.length > 0 ? cards.map((payment, idx) => {
                                                        return (
                                                            <Fragment key={idx}>
                                                                <ListGroupItem className="list-group-item-action listitem-pricing-card flex-column align-items-start">
                                                                    <div className="d-flex w-100 justify-content-between">
                                                                        <h5 className="mb-1">{payment.name}</h5>
                                                                        {/* <small style={{ color: "#f73164" }} className="text-secondary">{"Primary Method"}</small> */}
                                                                    </div>
                                                                    <p className="mb-1">{`**** **** **** ${payment.last4}`}</p>
                                                                    <small className="text-muted">{`Exp. ${payment.exp_month} ${payment.exp_year}`} <small className="float-right">Card Type: {payment.brand}</small></small>
                                                                </ListGroupItem>
                                                            </Fragment>
                                                        );
                                                    }) : <Fragment>
                                                        <div className="centered-both-ways">
                                                            <img src={require("../../../../../../assets/images/credit-cards.jpg")} className={"credit-cards-placeholder"} />
                                                        </div>
                                                    </Fragment>}
                                                </ListGroup>
                                            </CardBody>
                                        </Card>
                                    </Col>
                                    <Col lg="6" sm="12" md="6" xl="6">
                                        <Card className='shadow'>
                                            <CardHeader>
                                                <div className="product-page-details">
                                                    <h3>{"Course Details/Information (Overview)"}</h3>
                                                </div>
                                            </CardHeader>
                                            <CardBody>
                                                <Row>
                                                    <Col sm="4 tab-javascript">
                                                        <ListGroup className="nav-primary nav-pills">
                                                            <ListGroupItem href={null} className={activeTab === '1' ? 'list-group-item-action active' : ''} onClick={() => setActiveTabState('1')}>{Hometxt}</ListGroupItem>
                                                        </ListGroup>
                                                        <ListGroup>
                                                            <ListGroupItem href={null} className={activeTab === '2' ? 'list-group-item-action active' : ''} onClick={() => setActiveTabState('2')}>{Profile}</ListGroupItem>
                                                        </ListGroup>
                                                        <ListGroup>
                                                            <ListGroupItem href={null} className={activeTab === '3' ? 'list-group-item-action active' : ''} onClick={() => setActiveTabState('3')}>{Messages}</ListGroupItem>
                                                        </ListGroup>
                                                        <ListGroup>
                                                            <ListGroupItem href={null} className={activeTab === '4' ? 'list-group-item-action active' : ''} onClick={() => setActiveTabState('4')}>{Settings}</ListGroupItem>
                                                        </ListGroup>
                                                    </Col>
                                                    <Col sm="8">
                                                        <TabContent activeTab={activeTab}>
                                                        <TabPane tabId="1" className="fade show">{"1Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum."}</TabPane>
                                                        <TabPane tabId="2" className="fade show">{"2Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum."}</TabPane>
                                                        <TabPane tabId="3" className="fade show">{"3Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum."}</TabPane>
                                                        <TabPane tabId="4" className="fade show">{"4Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum."}</TabPane>
                                                        </TabContent>
                                                    </Col>
                                                </Row>
                                            </CardBody>
                                        </Card>
                                    </Col>
                                </Row>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, {  })(PurchaseCourseContentStripePaymentHelper);
