                    } else {
                        setValue(`concept${idxxxx - 1}`, val, { shouldValidate: true });
                    }
                }
                return prevState.filter((x => {
                    if (x.id === item.id) {
                        return false;
                    } else {
                        return true;
                    }
                }));
            });
        }
    }
