import React, { Fragment, useState, useEffect } from "react";
import "./styles.css";
import Breadcrumb from '../../../../../layout/breadcrumb';
import Dropzone from 'react-dropzone-uploader';
import { Container, Row, Col, Card, CardBody, CardHeader, Form, FormGroup, Label, Input, Button } from 'reactstrap';
import DatePicker from "react-datepicker";
import { useForm, Controller } from 'react-hook-form';
import { connect } from 'react-redux';
import { withRouter, Link } from 'react-router-dom';
import { ProjectTitle, ClientName, ProjectRate, ProjectStatus, ProgressLevel, ProjectSize, Small, Medium, Big, StartingDate, EndingDate, EnterSomeDetails, UploadProjectFile, Add, Cancel, Done, Doing } from '../../../../../constant';
import helpers from "./helpers/miscFunctions.js";
import CreateNewCoursePageOne from "./helpers/pages/pageOne/index.js";

const EducationalCoursesCreationHelper = ({  }) => {

    const { register, handleSubmit, control, reset, setValue, setError, clearErrors, formState: { errors }} = useForm({
        mode: "onTouched",
        reValidateMode: "onBlur"
    });

    const getUploadParams = ({ meta }) => { 
        return { 
          url: 'https://httpbin.org/post' 
        }
    }


    // called every time a file's `status` changes
    const handleChangeStatus = ({ meta, file }, status) => {
        console.log("handleChangeStatus ran...", meta, file, status);
    }

    const renderErrorsFormUploadContent = (e, errors) => {
        console.log("renderErrorsFormUploadContent ran...", e, errors);
    }
    const onSubmission = (e, data) => {
        console.log("onSubmission ran...", e, data);
    }
    return (
        <Fragment>
            <Breadcrumb parent="Project" title="Create Project" /> 
                <Container fluid={true}>
                    <CreateNewCoursePageOne />
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, {  })(EducationalCoursesCreationHelper);
