import React, { Fragment } from "react";
import ReactPlayer from 'react-player';


const renderImageOrVideo = ({ randomPreview }) => {
    if (randomPreview.type.includes("video")) {
        return (
            <Fragment>
                <div className="ribbon-wrapper-bottom">
                    <ReactPlayer key={randomPreview.id} controls={true} loop={true} muted={true} url={`${process.env.REACT_APP_ASSET_LINK}/${randomPreview.link}`} className="stretch-both-ways-topped" />
                </div>
            </Fragment>
        );
    } else {
        return (
            <Fragment>
                <div className="ribbon-wrapper-bottom">
                    <img key={randomPreview.id} src={`${process.env.REACT_APP_ASSET_LINK}/${randomPreview.link}`} className="stretch-both-ways-topped" />
                </div>
            </Fragment>
        );
    }
}
const MemoizedRenderVideoOrImage = React.memo(renderImageOrVideo);

export default {
    MemoizedRenderVideoOrImage
}
