import React, { Fragment, useState, useEffect } from 'react';
import { CardHeader, CardBody, CardFooter, Card, Row, Col, Container } from 'reactstrap';
import "./styles.css";
import Breadcrumb from '../../../../layout/breadcrumb';
import { connect } from "react-redux";
import axios from "axios";

const ViewAllBalanceRelatedInfoHelper = ({ userData }) => {

    useEffect(() => {
        const config = {
            params: {
                id: userData.uniqueId,
                accountType: userData.accountType
            }
        }
        axios.get(`${process.env.REACT_APP_BASE_URL}/gather/availiable/stripe/bal`, config).then((res) => {
            if (res.data.message === "Gathered balance!") {

                const { bal } = res.data;

                console.log(res.data);
            } else {
                console.log("err", res.data);

                NotificationManager.warning("An unknown error has occurred while attempting to fetch your related balance related information. Contact support if the problem persists!", "Unknown error has occurred.", 4750);
            }
        }).catch((err) => {
            console.log(err);

            NotificationManager.warning("An unknown error has occurred while attempting to fetch your related balance related information. Contact support if the problem persists!", "Unknown error has occurred.", 4750);
        })
    }, [])

    return (
        <Fragment>
            <Breadcrumb parent="View Balance (Pending, Availiable & Held)" title="View Balance & Other Related Critical Information" />
            <Container fluid={true}>
                <Row>
                    <Col sm="12" lg="12" md="12" xl="12">
                        <Card className={"shadow-card-balance"}>
                            <CardHeader className='b-l-info b-r-info'>
                                <h3 className='balance-title'>Below you will be able to find, manage and view your related account balances & other related info!</h3>
                            </CardHeader>
                            <CardBody className='b-l-secondary b-r-secondary'>
                                <Row>
                                    <Col sm="12" lg="4" md="4" xl="4">
                                        <Card>
                                            <CardHeader className="b-l-success border-3">
                                                <h5 className='header-balance-green'>Availiable</h5>
                                            </CardHeader>
                                            <CardBody>
                                                <p className='lead'>You currently <strong>availiable balance</strong> which you have <strong>immediate</strong> access to, these are funds you can currently withdrawl & use! Funds that are available to be transferred or paid out..</p>
                                                <hr />
                                            </CardBody>
                                        </Card>
                                    </Col>
                                    <Col sm="12" lg="4" md="4" xl="4">
                                        <Card>
                                            <CardHeader className="b-l-secondary border-3">
                                                <h5 className='header-balance-red'>Connect Reserved</h5>
                                            </CardHeader>
                                            <CardBody>
                                                <p className='lead'><strong>Funds held</strong> due to <strong>negative</strong> balances on hacker account types. These are <strong>"held"</strong> funds.</p>
                                                <hr />
                                            </CardBody>
                                        </Card>
                                    </Col>
                                    <Col sm="12" lg="4" md="4" xl="4">
                                        <Card>
                                            <CardHeader className="b-l-primary border-3">
                                                <h5 className='header-balance-primary'>Pending</h5>
                                            </CardHeader>
                                            <CardBody>
                                                <p className='lead'>These funds are <strong>"pending"</strong> funds which means they are <strong>not yet</strong> availiable due to the <strong>7-day rolling pay cycle</strong>. It generally takes a few days (7) for newly aquired funds to become availiable!</p>
                                                <hr />
                                            </CardBody>
                                        </Card>
                                    </Col>
                                </Row>
                            </CardBody>
                            <CardFooter className='b-l-info b-r-info'>

                            </CardFooter>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, { })(ViewAllBalanceRelatedInfoHelper);
