import React, { Component, Fragment } from 'react';
import Autocomplete from "react-autocomplete";
import axios from "axios";

class LocationSearchInput extends Component {
constructor(props) {
    super(props);

    this.state = { 
        address: '',
        results: []
    };
}

    handleChange = e => {
        const { value } = e.target;

        this.setState({
            address: value
        }, () => {
            axios.get(`https://api.tomtom.com/search/2/search/${this.state.address}.json?key=${process.env.REACT_APP_TOMTOM_API_KEY}&language=en-US&limit=10`).then((res) => {

                const { results } = res.data;

                console.log("Response from tomtom: ", res.data, results);

                this.setState({
                    results
                })
            }).catch((err) => {
                console.log("tomtom err: ", err);
            })
        })
    };
    render() {
        const { address, results } = this.state;

        console.log("this.state:", this.state);

        return (
        <Fragment>
            <Autocomplete
                getItemValue={(item) => item.address.freeformAddress}
                items={results}
                renderItem={(item, isHighlighted) => {
                    console.log(item);

                    return (
                        <div style={{ background: isHighlighted ? 'lightgray' : 'white' }}>
                            {item.address.freeformAddress}
                        </div>
                    );
                }}
                value={address}
                onChange={this.handleChange}
                onSelect={(val) => {
                    console.log("selected: ", val);
                }}
            />
        </Fragment>
        );
    }
}
export default LocationSearchInput;
