import React, { useState, useEffect, useMemo } from 'react';
import "./styles.css";
import { Container, Row, Col, Card, CardHeader, Textarea, CardBody, Input, Label, Form, FormGroup, InputGroup, InputGroupAddon, InputGroupText } from "reactstrap";
import Breadcrumb from '../../../../layout/breadcrumb';
import { connect } from "react-redux";
import SimpleMDE from "react-simplemde-editor";
import { WithContext as ReactTags } from 'react-tag-input';


const CreateNewBlogPostHelper = (props) => {

    const [ inputState, setInputState ] = useState({
        title: "",
        subtitle: "",
        body: "",
        hashtags: []
    });

    console.log("inputState", inputState);

    const handleInputChange = (e) => {
        const { name, value } = e;

        setInputState(prevState => {
            return {
                ...prevState,
                [name]: value
            }
        })
    }

    const autofocusNoSpellcheckerOptions = useMemo(() => {
        return {
          autofocus: false,
          spellChecker: false,
        }
    }, []);

    const handleDelete = (i) => {

        setInputState(prevState => {
            return {
                ...prevState,
                hashtags: prevState.hashtags.filter((tag, index) => index !== i)
            }
        })
    }
    const handleAddition = (tag) => {

        setInputState(prevState => {
            return {
                ...prevState,
                hashtags: [...prevState, tag]
            }
        })
    }

    const handleDrag = (tag, currPos, newPos) => {
        const tags = [...hashtags];
        const newTags = tags.slice();

        newTags.splice(currPos, 1);
        newTags.splice(newPos, 0, tag);

        setInputState(prevState => {
            return {
                ...prevState,
                hashtags: newTags
            }
        })
    }

    return (
        <div>
            <Breadcrumb parent="Previously Received Review(s)" title="Previously Received Review's From Contracted Work" />
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className='shadow'>
                            <CardHeader className='b-l-primary b-r-primary'>
                                <h3>Create a new blog post and share your knowledge with the user's of our platform! This is an <em style={{ textDecorationLine: "underline", color: "#7366ff" }}>excellent</em> way to promote your account and/or knowledge showing you know what you're doing. Share your thoughts & knowledge today!</h3>
                            </CardHeader>
                            <CardBody>
                                <Form>
                                    <FormGroup className=" m-form__group">
                                        <Label>Blog Title (Main display title) ~ <em style={{ color: "red", textDecorationLine: "underline" }}>You must enter between 35 - 125 characters before posting..</em></Label>
                                        <InputGroup>
                                            <InputGroupAddon addonType="prepend"><InputGroupText>{"Blog Title"}</InputGroupText></InputGroupAddon>
                                            <Input className="form-control" onChange={(e) => handleInputChange(e)} name={"title"} type="text" placeholder={"Enter your 'blog title'..."} />
                                        </InputGroup>
                                    </FormGroup>
                                    <FormGroup className=" m-form__group">
                                        <Label>Blog Sub-Title (Main display sub-title) ~ <em style={{ color: "red", textDecorationLine: "underline" }}>You must enter between 35 - 125 characters before posting..</em></Label>
                                        <InputGroup>
                                            <InputGroupAddon addonType="prepend"><InputGroupText>{"Blog Title"}</InputGroupText></InputGroupAddon>
                                            <Input className="form-control" onChange={(e) => handleInputChange(e)} name={"subtitle"} type="text" placeholder={"Enter your 'blog sub-title'..."} />
                                        </InputGroup>
                                    </FormGroup>
                                    <FormGroup className=" m-form__group">
                                        <Label>Blog Main Content ~ <em style={{ color: "red", textDecorationLine: "underline" }}>You must enter between 500 - 3500 characters before posting..</em></Label>
                                        <SimpleMDE
                                            id="post-content-custom"
                                            placeholder={`## Summary:
                                                [Add summary of the vulnerability]

                                                ## Steps to Reproduce:
                                                [Add details for how we can reproduce the issue. Please ensure reproducibility of the issue.]

                                                1. [add step]
                                                2. [add step]
                                                3. [add step]

                                                ## Impact
                                                [This session is very important as it helps us to assess the severity of the issue. To help you with filling in this session, we think that answering the following questions may help:
                                                How does the issue affect the business or the user? 
                                                What can the attacker get through the issue? 
                                                Can the issue be escalated further? If so, how?

                                                ## Mitigation
                                                [Please give a brief description of how the bug could be fixed.]


                                                ## Supporting Material/References:
                                                [list any additional material (e.g. screenshots, logs, etc.)]

                                                * [attachment / reference]
                                            `}
                                            onChange={(value) => setInputState(prevState => {
                                                return {
                                                    ...prevState,
                                                    body: value
                                                }
                                            })}
                                            value={inputState.body}
                                            options={autofocusNoSpellcheckerOptions}
                                        />
                                    </FormGroup>
                                    <FormGroup className=" m-form__group">
                                        <ReactTags 
                                            autofocus={false}
                                            tags={inputState.hashtags}
                                            classNames={{
                                                tags: 'tagsClass',
                                                tagInput: 'tagInputClass',
                                                tagInputField: 'form-control',
                                                selected: 'selectedClass',
                                                tag: 'badge badge-primary custom-tag-streaming',
                                                remove: 'removeClass',
                                                suggestions: 'suggestionsClass',
                                                activeSuggestion: 'activeSuggestionClass'
                                            }}
                                            placeholder={"(OPTIONAL) Add hashtags to your comment..."}
                                            maxLength={10}
                                            // suggestions={suggestions}
                                            handleDelete={handleDelete}
                                            handleAddition={handleAddition}
                                            handleDrag={handleDrag}
                                            delimiters={delimiters} 
                                        />
                                    </FormGroup>
                                </Form>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </div>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, {  })(CreateNewBlogPostHelper);
