            resolve({
              newDateArray,
              startDateArray,
              endDateArray
            });
          }
        }
      }).then(values => {
        const {
          startDateArray,
          endDateArray,
          newDateArray
        } = values;
        console.log(startDateArray, endDateArray); // min & max dates

        const maxDate = new Date(Math.max.apply(null, endDateArray));
        const minDate = new Date(Math.min.apply(null, startDateArray));
        const maxedDate = moment(maxDate).startOf('day').toString(); // difference in days
        // const diffDays = Math.round(Math.abs((firstDate - secondDate) / oneDay));

        const mappedSelectedDates = [];
        const dismissDates = [];

        for (let index = 0; index < newDateArray.length; index++) {
          const date = newDateArray[index];

          for (let d = new Date(date.startDate); d <= new Date(date.endDate); d.setDate(d.getDate() + 1)) {
            const startOfDay = moment(new Date(d)).startOf('day').toString();

            if (!mappedSelectedDates.includes(startOfDay)) {
              mappedSelectedDates.push(startOfDay);
            }
          }

          if (newDateArray.length - 1 === index) {
            for (let d = minDate; d <= new Date(new Date(maxedDate).setDate(d.getDate() + 1)); d.setDate(d.getDate() + 1)) {
              const formatedLoopItem = moment(new Date(d)).startOf('day').toString(); // console.log("formatedLoopItem", formatedLoopItem, maxedDate);

              if (!mappedSelectedDates.includes(formatedLoopItem)) {
                dismissDates.push(new Date(d));

                if (formatedLoopItem === maxedDate) {
                  // set disabled days array
                  setDisabledDaysState(dismissDates); // set SELECTED/PREVIOUSLY-PICKED days state

                  setDateRanges(newDateArray); // display content finally!

                  setDatesReady(true);
                }
              } else {
                if (formatedLoopItem === maxedDate) {
                  // set disabled days array
                  setDisabledDaysState(dismissDates); // set SELECTED/PREVIOUSLY-PICKED days state

                  setDateRanges(newDateArray); // display content finally!

                  setDatesReady(true);
                }
              }
            }
          }
        } // min and max dates


        console.log("maxDate", maxDate);
        console.log("minDate", minDate);
      }); // update physicalOrDigitalHackOptions options 

      switch (listing.typeOfHack.value) {
        case "physical-hack":
          setPhysicalOrDigitalHackOptionsState([{
            label: "BOTH (Digital/Physical hack's) Hack Type's",
            value: "both-assets",
            isDisabled: true
          }, {
            label: "Digital/Internet-Hack ONLY",
            value: "digital-internet-hack",
            isDisabled: true
          }, {
            label: "Physical-Hack ONLY",
            value: "physical-hack"
          }]);
          break;

        case "":
          setPhysicalOrDigitalHackOptionsState([{
            label: "BOTH (Digital/Physical hack's) Hack Type's",
            value: "both-assets",
            isDisabled: true
          }, {
