import axios from 'axios';
import moment from "moment";
import _ from "lodash";
import "./styles.css";
import ReactPlayer from 'react-player';
import { withRouter } from "react-router-dom";

class MainHackerDirectoryDisplayHelper extends Component {
constructor(props) {
