    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
};

const itemsPerPage = 10;

const CourseListHelper = ({ userData }) => {

    const history = useHistory();

    const [ progress, setProgress ] = useState(10);
    const [learningData, setLearningData] = useState([{
