import React, { Fragment } from "react";
import axios from "axios";
import { connect } from "react-redux";
import { CardBody, CardHeader, Card, Col, Container, Row } from "reactstrap";
import "./styles.css";
import Select from "react-dropdown-select";

const options = [{
    label: "one",
    value: "one"
}, {
    label: "two",
    value: "two"
}, {
    label: "three",
    value: "three"
}, {
    label: "four",
    value: "four"
}, {
    label: "five",
    value: "five"
}, {
    label: "six",
    value: "six"
}, {
    label: "seven",
    value: "seven"
}]

const CreateNewForumPostingHelper = ({ userData }) => {
    return (
        <Fragment>
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card style={{ marginTop: "50px" }}>
                            <CardHeader className="b-l-info b-r-info">
                                <h3>Create a new forum post (Must be associated with a <a className="create-forum-post-a" href={null}>community</a>)</h3>
                            </CardHeader>
                            <CardBody>
                                <Select options={options} onChange={(values) => this.setValues(values)} />
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, {  })(CreateNewForumPostingHelper);
