import React, { Fragment, useState, useEffect } from "react";
import ReactDOM from 'react-dom';
import ReactPlayer from "react-player";
import _ from "lodash";
import "./styles.css";
import { SRLWrapper } from "simple-react-lightbox";
import { NotificationManager } from "react-notifications";

const renderPictureOrVideoLast = (file, index, openLightbox) => {
    if (file !== null && _.has(file, "link")) {
        if (file.type.includes("video")) {
            // video logic
            return (
                <Fragment>
                    <li onClick={() => {
                        // open modal and display
                        NotificationManager.warning("The connected 'lightbox' display component does NOT allow video playback so the lightbox will not open upon clicking this video element - please try an image instead!", "Try an image instead of a video!", 4750)
                    }}>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} className={"img-fluid"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        } else {
            // image logic
            return (
                <Fragment>
                    <li onClick={() => {
                        // open modal and display
                        openLightbox(index - 1);
                    }}>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <img className="img-fluid" alt="post1" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        }  
    } else {
        // image logic - DEFAULT.
        return (
            <Fragment>
                <li onClick={() => {
                    // open modal and display
                    openLightbox(index - 1);
                }}>
                    <div className="latest-post profile-pic-video-sidebar-custom">
                        <img className="img-fluid" alt="post1" src={process.env.REACT_APP_PLACEHOLDER_IMAGE} />
                    </div>
                </li>
            </Fragment>
        );
    } 
}
const RenderGalleryModalHackerProfileHelper = ({ closeLightbox, user, openLightbox }) => {

    const [ picturesArr, setPicturesArr ] = useState([]);

    const callbacks = {
        onSlideChange: object => console.log(object),
        onLightboxOpened: object => console.log(object),
        onLightboxClosed: object => console.log(object),
        onCountSlides: object => console.log(object)
    };

    const options = {
        settings: {
            overlayColor: "rgba(23, 33, 175, 0.7)",
            autoplaySpeed: 2250,
            boxShadow: true
        },
        buttons: {
            backgroundColor: "#1b5245",
            iconColor: "rgba(126, 172, 139, 0.8)",
        },
        thumbnails: {

        },
        caption: {
            captionColor: "#a6cfa5",
            captionTextTransform: "uppercase",
        }
    };

    useEffect(async () => {

        if (typeof user.profilePicsVideos !== "undefined" && user.profilePicsVideos.length > 0) {
            const captionArray = [];
            // pictures exist - run setup logic
            const newlyStructuredProfilePicsVids = await user.profilePicsVideos.map((el, idx, row) => {
                console.log("el", el);
                if (idx + 1 === row.length) {
                    // Last one.
                    let result = {
                        src: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        type: el.type.includes("image") ? "photo" : "video",
                        caption: `You're viewing the ${el.name} file...`,
                        autoplay: false,
                        showControls: true,
                        thumbnail: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        link: el.link,
                        className: "caption-thumbnail-custom-hacker-profile"
                    };
                    // return constructed obj for lightbox
                    return result;
                } else {
                    // Not last one.
                    let resultNotLast = {
                        src: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        type: el.type.includes("image") ? "photo" : "video",
                        caption: `You're viewing the ${el.name} file...`,
                        autoplay: false,
                        showControls: true,
                        thumbnail: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        link: el.link,
                        className: "caption-thumbnail-custom-hacker-profile"
                    }
                    // return constructed obj for lightbox
                    return resultNotLast;
                }
            });
            // wait for .mapped to finish then set the local state.
            if (newlyStructuredProfilePicsVids) {
                setPicturesArr(newlyStructuredProfilePicsVids);
            }
        }
    }, []);
    return (
        <Fragment>
            <SRLWrapper callbacks={callbacks} options={options}>
                <div className={"gallery-spacer-hacker-profile"}>
                    <ul className="text-center customized-row-profile-hacker-pics">
                        {typeof picturesArr !== "undefined" && picturesArr.length > 0 ? picturesArr.map((file, idxxx) => renderPictureOrVideoLast(file, idxxx, openLightbox)) : null}
                    </ul>
                </div>
            </SRLWrapper>
        </Fragment>
    );
}
export default {
    renderPictureOrVideoLast,
    RenderGalleryModalHackerProfileHelper
};
