import 'react-notifications/lib/notifications.css';
import './styles/bootstrap.min.css';
import './styles/animate.css';
import './styles/boxicons.min.css';
import './styles/flaticon.css';
import '../node_modules/react-modal-video/css/modal-video.min.css';
import 'react-accessible-accordion/dist/fancy-example.css';
import './styles/style.css';
import './styles/responsive.css';
import 'react-phone-number-input/style.css'
import 'react-confirm-alert/src/react-confirm-alert.css';
// components
import React, { Fragment,useState,useEffect } from 'react';
import ReactDOM from 'react-dom';
import './index.scss';
import App from './components/app';
import * as serviceWorker from './serviceWorker';
import { Provider } from 'react-redux';
import {BrowserRouter,Switch,Route,Redirect} from 'react-router-dom'
import { CSSTransition,TransitionGroup } from 'react-transition-group'
import {routes} from './route';
import ConfigDB from './data/customizer/config'
import { classes } from './data/layouts';
import {NotificationContainer} from 'react-notifications';
import Index from "./pages/index.js";
import { RecoilRoot } from 'recoil';
import SignIn from "./pages/sign-in.js";
import SignUp from "./pages/sign-up.js";
import { store } from "./redux/store/store.js";
import ForgotPassword from "./pages/forgot-password.js";
import Contact from "./pages/contact.js";
import axios from "axios";
import _ from "lodash";
import ProtectedRoute from "./route/protected/protectedRoute.js";
import ReactLoading from 'react-loading';


const Root = (props) =>  {

  const [anim, setAnim] = useState("");
  const [ ready, setReady ] = useState(false);
  const animation = localStorage.getItem("animation") || ConfigDB.data.router_animation || 'fade'
  const abortController = new AbortController();
  const defaultLayoutObj = classes.find(item => Object.values(item).pop(1) === 'compact-wrapper');
  const layout = localStorage.getItem('layout') ||  Object.keys(defaultLayoutObj).pop();

  const refreshTokenApiRequest = () => {


    console.log(store.getState());

    const accountData = store.getState().auth.data;

    if (!_.isEmpty(accountData)) {
      axios.post(`http://localhost:5000/refresh/token/${accountData.accountType === "hackers" ? "hacker" : "employer"}`, null, {
        withCredentials: true
      }).then((res) => {
        if (res.data.message === "Gathered refresh token!") {
          console.log(res.data);
        } else {
          console.log("err", res.data);
        }
      }).catch((err) => {
        console.log(err);
      })
    } else {
      console.log("store user data is empty.");
    }
  }

  useEffect(() => {
      setAnim(animation);

      setTimeout(refreshTokenApiRequest, 5 * 60 * 1000);

      setTimeout(() => {
        refreshTokenApiRequest();
      }, 500);

      setTimeout(() => {
        setReady(true);
      }, 500);

      console.ignoredYellowBox = ["Warning: Each", "Warning: Failed"];
      console.disableYellowBox = true;
      return function cleanup() {
          abortController.abort();
        }
      // eslint-disable-next-line
  }, []);

  const renderConditional = () => {
    const accountData = store.getState().auth.data;

    if (ready === true) {
      if (_.isEmpty(accountData)) {
        return (
          <div style={{ flexDirection: "row", display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
            <ReactLoading type={"cubes"} color={"#00acee"} height={350} width={600} />
          </div>
        );
      } else {
        return (
          <Switch>
            <Route exact path="/" component={Index} />
            <Route exact path="/sign-in" component={SignIn} />
            <Route exact path="/sign-up" component={SignUp} />
            <Route exact path="/forgot-password" component={ForgotPassword} />
            <Route exact path="/contact" component={Contact} />
            <App>
              <TransitionGroup>
                {routes.map(({ path, Component }) => (
                    <Route key={path} exact path={`${process.env.PUBLIC_URL}${path}`}>
                        {({ match }) => (
                            <CSSTransition 
                            in={match != null}
                            timeout={100}
                            classNames={anim} 
                            unmountOnExit
                            >
                            <div>
                              <ProtectedRoute component={Component} />
                            </div>
                            </CSSTransition> 
                        )}
                    </Route>
                    ))}
              </TransitionGroup>
            </App>
          </Switch>
        );
      }
    } else {
      return (
        <div style={{ flexDirection: "row", display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <ReactLoading type={"cubes"} color={"#00acee"} height={350} width={600} />
        </div>
      );
    }
  }
  return(
    <Fragment>
      <Provider store={store}>
      <RecoilRoot>
      <NotificationContainer />
      <BrowserRouter>
      {renderConditional()}
      </BrowserRouter>
      </RecoilRoot>
      </Provider>
    </Fragment>
  )
}
ReactDOM.render(<Root/>,
  document.getElementById('root')
);

serviceWorker.unregister();
