import React, { Fragment, useState, useEffect } from "react";
import ReactDOM from 'react-dom';
import ReactPlayer from "react-player";
import _ from "lodash";
import "./styles.css";
import { Modal } from 'react-responsive-modal';
import { NotificationManager } from "react-notifications";
import Slider from "react-slick";
import { Container, Col, Card, Row } from "reactstrap";

const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1
};

const renderPictureOrVideoLast = (file, index, onCloseModal, setSelectedModalIndex) => {
    if (file !== null && _.has(file, "link")) {
        if (file.type.includes("video")) {
            // video logic
            return (
                <Fragment>
                    <li onClick={() => {
                        // set current index for modal
                        setSelectedModalIndex(index);
                        // open modal and display
                        onCloseModal(true);
                    }}>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} className={"img-fluid"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        } else {
            // image logic
            return (
                <Fragment>
                    <li onClick={() => {
                        // set current index for modal
                        setSelectedModalIndex(index);
                        // open modal and display
                        onCloseModal(true);
                    }}>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <img className="img-fluid" alt="post1" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        }  
    } else {
        // image logic - DEFAULT.
        return (
            <Fragment>
                <li onClick={() => {
                    // set current index for modal
                    setSelectedModalIndex(index);
                    // open modal and display
                    onCloseModal(true);
                }}>
                    <div className="latest-post profile-pic-video-sidebar-custom">
                        <img className="img-fluid" alt="post1" src={process.env.REACT_APP_PLACEHOLDER_IMAGE} />
                    </div>
                </li>
            </Fragment>
        );
    } 
}
const RenderGalleryModalHackerProfileHelper = ({ onCloseModal, user, isOpen, modalIndexSelected, setSelectedModalIndex }) => {

    const [ picturesArr, setPicturesArr ] = useState([]);
    const [ captions, setCaptions ] = useState([]);

    useEffect(async () => {

        if (typeof user.profilePicsVideos !== "undefined" && user.profilePicsVideos.length > 0) {
            const captionArray = [];
            // pictures exist - run setup logic
            const newlyStructuredProfilePicsVids = await user.profilePicsVideos.map((el, idx, row) => {
                console.log("el", el);
                if (idx + 1 === row.length) {
                    // Last one.
                    let result = {
                        src: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        type: el.type.includes("image") ? "photo" : "video",
                        caption: `You're viewing the ${el.name} file...`,
                        autoplay: false,
                        showControls: true,
                        thumbnail: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        link: el.link,
                        className: "caption-thumbnail-custom-hacker-profile"
                    };
                    let caption = (
                        <div className="SRLCustomCaption lightbox-custom-caption">
                          <span className="SRLCustomCaption">You're viewing {el.name}</span>
                        </div>
                    );
                    // push appropriate caption for lightbox
                    captionArray.push({ id: idx, caption });
                    // return constructed obj for lightbox
                    return result;
                } else {
                    // Not last one.
                    let resultNotLast = {
                        src: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        type: el.type.includes("image") ? "photo" : "video",
                        caption: `You're viewing the ${el.name} file...`,
                        autoplay: false,
                        showControls: true,
                        thumbnail: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        link: el.link,
                        className: "caption-thumbnail-custom-hacker-profile"
                    }
                    let caption = (
                        <div className="SRLCustomCaption lightbox-custom-caption">
                          <span className="SRLCustomCaption">You're viewing {el.name}</span>
                        </div>
                    );
                    // push appropriate caption for lightbox
                    captionArray.push({ id: idx, caption });
                    // return constructed obj for lightbox
                    return resultNotLast;
                }
            });
            // wait for .mapped to finish then set the local state.
            if (newlyStructuredProfilePicsVids) {
                setCaptions(captionArray);
                setPicturesArr(newlyStructuredProfilePicsVids);
            }
        }
    }, []);

    return (
        <Fragment>
            <Modal classNames={{
                overlay: 'hackerProfileLightboxDisplayOverlay',
                modal: 'hackerProfileLightboxDisplayModal',
            }} open={isOpen} onClose={onCloseModal} center>
                <Container fluid={true}>
                    <Row>
                        <div className={"centered-both-ways"}>
                            <Col sm="12" md="12" lg="12" xl="12">
                                <div className={"centered-both-ways"}>
                                    <Slider {...settings}>
                                        {typeof picturesArr !== "undefined" && picturesArr.length > 0 ? picturesArr.map((file, idxxx) => {
                                            return (
                                                <Fragment key={idxxx}>
                                                    <Col sm="12" lg="12" xl="12" md="12" className="xl-100 box-col-12">
                                                        <Card className="height-equal">
                                                            <div className="calender-widget">
                                                                <div className="cal-img">
                                                                    <img src={file.src} className={"hacker-gallery-modal-pic"} />
                                                                </div>
                                                                <div className="cal-date">
                                                                <h5>{"25"}<br />{"APRIL"}</h5>
                                                                </div>
                                                                <div className="cal-desc text-center card-body">
                                                                <h6 className="f-w-600">{"I must explain to you how all this mistaken idea truth"}</h6>
                                                                <p className="text-muted mt-3 mb-0">{"Denouncing pleasure and praising pain was born and I will give you a complete account of the system, and expound the actual teachings of the great explorer of the truth,Letraset sheets containing Lorem Ipsum passages."}</p>
                                                                </div>
                                                            </div>
                                                        </Card>
                                                    </Col>
                                                </Fragment>
                                            );
                                        }) : null}
                                    </Slider>
                                </div>
                            </Col>
                        </div>
                    </Row>
                </Container>
            </Modal>
        </Fragment>
    );
}
export default {
    renderPictureOrVideoLast,
    RenderGalleryModalHackerProfileHelper
};
