import React, { Fragment, useEffect, useState } from 'react';
import "./styles.css";
import { Container, Row, Col, Card, CardHeader, CardBody, Button, Media, ListGroup, ListGroupItem } from "reactstrap";
import Breadcrumb from '../../../../../../../layout/breadcrumb';
import ReactPlayer from 'react-player';
import { useParams } from "react-router-dom";
import axios from "axios";
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton';
import { NotificationManager } from 'react-notifications';
import { connect } from "react-redux";

const ViewIndividualPurchasedCourseDataHelper = ({ userData }) => {

    const { id } = useParams();

    const [ courseData, setCourseData ] = useState(null);
    const [ ready, setReady ] = useState(false);

    useEffect(() => {
        const configuration = {
            params: {
                uniqueId: userData.uniqueId,
                accountType: userData.accountType,
                courseID: id
            }
        }

        axios.get(`${process.env.REACT_APP_BASE_URL}/gather/purchased/course/data/profile`, configuration).then((res) => {
            if (res.data.message === "Successfully gathered course!") {
                console.log(res.data);

                const { course } = res.data;

                for (let index = 0; index < courseData.mainData.pageTwoData.courseContentSections.length; index++) {
                    const courseSectionData = courseData.mainData.pageTwoData.courseContentSections[index];
                    courseSectionData["timespan"] = accordionValuesChanged(courseSectionData);   
                }

                setCourseData(course);
                setReady(true);
            } else {
                console.log("Err", res.data);

                NotificationManager.error("There was an error that occurred after trying to retrieve the required data, try reloading this page or contact support if this problem continue to persist!", "An unknown error has occurred!", 4750);
            }
        }).catch((err) => {
            console.log(err);

            NotificationManager.error("There was an error that occurred after trying to retrieve the required data, try reloading this page or contact support if this problem continue to persist!", "An unknown error has occurred!", 4750);
        })
    }, []);

    console.log("courseData", courseData);

    const formatFileSize = (bytes) => {
        const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
        if (bytes == 0) return '0 Byte';
        const i = parseInt(Math.floor(Math.log(bytes) / Math.log(1024)));
        return Math.round(bytes / Math.pow(1024, i), 2) + ' ' + sizes[i];
    }
    const accordionValuesChanged = async (section) => {
        // select first item of array as only item will ever return
        const currentlySelected = section.id;
        // find matching value
        const matchedValue = courseData.mainData.pageTwoData.courseContentSections.findIndex((item) => item.id === currentlySelected);
        // matched item
        const finalMatch = courseData.mainData.pageTwoData.courseContentSections[matchedValue];
        // create video tag to figure out duration of each video
        const video = document.createElement("video");
        // assign video attributes
        video.src = `${process.env.REACT_APP_ASSET_LINK}/${finalMatch.video.link}`;
        video.preload = "metadata";
        // return result
        video.addEventListener("loadedmetadata", () => {
            return Math.round(video.duration);
        });
    }
    const calculateMin = (minutes) => {
        return (minutes / 60).toFixed(2);
    }
    const renderRightMappedBar = () => {
        if (ready === true) {
            return (
                <Fragment>
                    <ListGroup>
                        {courseData.mainData.pageTwoData.courseContentSections.map((section, index) => {
                            console.log("section", section)
                            return (
                                <Fragment key={index}>
                                    <ListGroupItem className="list-group-item-action flex-column align-items-start">
                                        <div className="d-flex w-100 justify-content-between">
                                            <h5 className="sectiontitle-course"><strong style={{ color: "#f73164" }}>{`Section ${index + 1}) `}</strong> {section.sectionTitle}</h5>
                                        </div>
                                        <p className="formatted-size-course">{formatFileSize(section.video.size)} - {section.timespan}</p>
                                    </ListGroupItem>
                                </Fragment>
                            );
                        })}
                    </ListGroup>
                </Fragment>
            );
        } else {
            return (
                <Fragment>
                    <SkeletonTheme baseColor="#c9c9c9" highlightColor="#444">
                        <p>
                            <Skeleton count={40} />
                        </p>
                    </SkeletonTheme>
                </Fragment>
            );
        }
    }

    return (
        <Fragment>
            <Breadcrumb parent="Viewing Course Data/Information (Purchased Content).." title={`Course Data Is Now Available`}/>
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className='shadow'>
                            <CardHeader className='b-l-info b-r-info'>
                                <h3>{ready === true ? courseData.mainData.pageOneData.mainData.courseTitle : "Still loading course title..."}</h3>
                            </CardHeader>
                            <CardBody>
                                <Row>
                                    <Col sm="12" md="9" lg="9" xl="9">
                                        <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} height={"625px"} className={"custom-player-course-content"} wrapper={"div"} url={"https://cybersecurity-platform.s3.amazonaws.com/09d20510-781c-469b-96e9-f64a6de85557"} />
                                    </Col>
                                    <Col sm="12" md="3" lg="3" xl="3">
                                        {renderRightMappedBar()}
                                    </Col>
                                </Row>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, {  })(ViewIndividualPurchasedCourseDataHelper);
