import React, { Fragment } from "react";
import ReactPlayer from "react-player";
import _ from "lodash";
import { Media } from 'reactstrap';


const renderPictureOrVideoLast = (file) => {
    if (file !== null && _.has(file, "link")) {
        if (file.type.includes("video")) {
            // video logic
            return (
                <Fragment>
                    <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} className={"profile-picture-account media-body rounded-circle-video-custom"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                </Fragment>
            );
        } else {
            // image logic
            return (
                <Fragment>
                    <img className="img-fluid" alt="post1" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                </Fragment>
            );
        }  
    } else {
        // image logic - DEFAULT.
        return (
            <Fragment>
                <Media className="rounded-circle" body alt="profile-picture-sub" src={process.env.REACT_APP_PLACEHOLDER_IMAGE} data-intro="This is Profile image" />
            </Fragment>
        );
    } 
}
export default {
    renderPictureOrVideoLast
};
