import React, { useState, useEffect } from 'react';
import "./styles.css";
import { Container, Row, Col, Card, CardHeader, CardBody, Input, Label, Form, FormGroup, InputGroup, InputGroupAddon, InputGroupText } from "reactstrap";
import Breadcrumb from '../../../../layout/breadcrumb';
import { connect } from "react-redux";


const CreateNewBlogPostHelper = (props) => {

    const [ inputState, setInputState ] = useState({});

    console.log("inputState", inputState);

    const handleInputChange = (e) => {
        const { name, value } = e;

        setInputState(prevState => {
            return {
                ...prevState,
                [name]: value
            }
        })
    }

    return (
        <div>
            <Breadcrumb parent="Previously Received Review(s)" title="Previously Received Review's From Contracted Work" />
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className='shadow'>
                            <CardHeader className='b-l-primary b-r-primary'>
                                <h3>Create a new blog post and share your knowledge with the user's of our platform! This is an <em style={{ textDecorationLine: "underline", color: "#7366ff" }}>excellent</em> way to promote your account and/or knowledge showing you know what you're doing. Share your thoughts & knowledge today!</h3>
                            </CardHeader>
                            <CardBody>
                                <Form>
                                    <FormGroup className=" m-form__group">
                                        <Label>Blog Title (Main display title)</Label>
                                        <InputGroup>
                                            <InputGroupAddon addonType="prepend"><InputGroupText>{"Blog Title"}</InputGroupText></InputGroupAddon>
                                            <Input className="form-control" onChange={(e) => handleInputChange(e)} name={"title"} type="text" placeholder={"Enter your 'blog title'..."} />
                                        </InputGroup>
                                    </FormGroup>
                                    <FormGroup className=" m-form__group">
                                        <Label>Blog Sub-Title (Main display sub-title)</Label>
                                        <InputGroup>
                                            <InputGroupAddon addonType="prepend"><InputGroupText>{"Blog Title"}</InputGroupText></InputGroupAddon>
                                            <Input className="form-control" onChange={(e) => handleInputChange(e)} name={"subtitle"} type="text" placeholder={"Enter your 'blog sub-title'..."} />
                                        </InputGroup>
                                    </FormGroup>
                                </Form>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </div>
    );
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, {  })(CreateNewBlogPostHelper);
