import React, { Fragment, useState, useEffect } from "react";
import ReactPlayer from "react-player";
import _ from "lodash";
import "./styles.css";
import ReactImageVideoLightbox from 'react-image-video-lightbox';
import { Modal } from 'react-responsive-modal';

const renderPictureOrVideoLast = (file, onCloseModal, index) => {
    if (file !== null && _.has(file, "link")) {
        if (file.type.includes("video")) {
            // video logic
            return (
                <Fragment>
                    <li onClick={() => {
                        setSelectedModalIndex(index);
                        // open modal and display
                        onCloseModal(true);
                    }}>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <ReactPlayer playing={true} loop={true} muted={true} width={"100%"} className={"img-fluid"} wrapper={"div"} url={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        } else {
            // image logic
            return (
                <Fragment>
                    <li onClick={() => {
                        setSelectedModalIndex(index);
                        // open modal and display
                        onCloseModal(true);
                    }}>
                        <div className="latest-post profile-pic-video-sidebar-custom">
                            <img className="img-fluid" alt="post1" src={`${process.env.REACT_APP_ASSET_LINK}/${file.link}`} />
                        </div>
                    </li>
                </Fragment>
            );
        }  
    } else {
        // image logic - DEFAULT.
        return (
            <Fragment>
                <li onClick={() => {
                    setSelectedModalIndex(index);
                    // open modal and display
                    onCloseModal(true);
                }}>
                    <div className="latest-post profile-pic-video-sidebar-custom">
                        <img className="img-fluid" alt="post1" src={process.env.REACT_APP_PLACEHOLDER_IMAGE} />
                    </div>
                </li>
            </Fragment>
        );
    } 
}
const RenderGalleryModalHackerProfileHelper = ({ onCloseModal, isOpen, user }) => {

    const [ picturesArr, setPicturesArr ] = useState([]);

    useEffect(async () => {

        if (typeof user.profilePicsVideos !== "undefined" && user.profilePicsVideos.length > 0) {
            // pictures exist - run setup logic
            const newlyStructuredProfilePicsVids = await user.profilePicsVideos.map((el, idx, row) => {
                if (idx + 1 === row.length) {
                    // Last one.
                    let result = {
                        url: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        type: el.type.includes("image") ? "photo" : "video",
                        altTag: "profile-picture-or-video"
                    };
                    console.log("last reached in contructor loop!");

                    return result;
                } else {
                    // Not last one.
                    let resultNotLast = {
                        url: `${process.env.REACT_APP_ASSET_LINK}/${el.link}`,
                        type: el.type.includes("image") ? "photo" : "video",
                        altTag: "profile-picture-or-video"
                    }
                    console.log("last reached in contructor loop!");

                    return resultNotLast;
                }
            });
            console.log("mark jotted.", newlyStructuredProfilePicsVids)
            if (newlyStructuredProfilePicsVids) {
                setPicturesArr(newlyStructuredProfilePicsVids);
            }
        }
    }, []);

    const galleryCallback = (data) => {
        console.log("galleryCallback callback :", data);
    }
    return (
        <Fragment>
            <Modal classNames={{
                overlay: 'hackerProfileLightboxDisplayOverlay',
                modal: 'hackerProfileLightboxDisplayModal',
            }} clas open={isOpen} onClose={onCloseModal} center>
                <ReactImageVideoLightbox
                    data={picturesArr}
                    startIndex={0}
                    showResourceCount={true}
                    onCloseCallback={galleryCallback}
                    onNavigationCallback={(currentIndex) =>
                        console.log(`Current index: ${currentIndex}`)
                    }
                />
            </Modal>
        </Fragment>
    );
}
export default {
    renderPictureOrVideoLast,
    RenderGalleryModalHackerProfileHelper
};
