import React, { Fragment } from 'react';
import { useForm } from 'react-hook-form';
import { Row, Col, Form, Label, Input } from 'reactstrap';

const PageOneVideoCreationHelper = () => {
    const { register, handleSubmit, formState: { errors } } = useForm(); 

    const onSubmit = data => {

        if (data !== '') {
            alert('You submitted the form and stuff!');
        } else {
            errors.showMessages();
        }
    };
    return (
        <Fragment>
            <Row>
                <Col sm="12">
                    <Form className="needs-validation" onSubmit={handleSubmit(onSubmit, (errorsss, e) => {
                        console.log("errrrrrs", errorsss, e);
                    })}>
                        <div className="form-row">

                            <Col md="12 mb-3">
                                <Label>Video Title</Label>
                                <Input {...register("videoTitle", { required: {
                                    value: true,
                                    message: "You MUST enter a value between 15-125 characters"
                                }, minLength: {
                                    value: 15,
                                    message: "You must enter AT Least 15 characters"
                                }, maxLength: {
                                    value: 125,
                                    message: "You may ONLY enter 125 characters or less"
                                }})} className="form-control" name="videoTitle" type="text" placeholder="Enter your 'video title' for this tutorial.." />
                                {/* {errors.videoTitle ? <span className="span-tooltip">{errors.videoTitle}</span> : null} */}
                            </Col>

                            <Col md="12 mb-3">
                                <Label>Video Subtitle</Label>
                                <Input {...register("videoSubtitle", { required: {
                                    value: true,
                                    message: "You MUST enter a value between 15-125 characters"
                                }, minLength: {
                                    value: 15,
                                    message: "You must enter AT Least 15 characters"
                                }, maxLength: {
                                    value: 125,
                                    message: "You may ONLY enter 125 characters or less"
                                }})} className="form-control"  name="videoSubtitle" type="text" placeholder="Enter your 'subtitle' for this tutorial.." />
                                {/* {errors.videoSubtitle ? <span className="span-tooltip">{errors.videoSubtitle}</span> : null} */}
                            </Col>

                        </div>
                    </Form>
                </Col>
            </Row>
        </Fragment>
    );
};

export default PageOneVideoCreationHelper;
