import React, { Fragment ,useState} from 'react';
import {Col, Card, CardHeader, CardBody, Button, Media, Form, FormGroup, Input , Collapse, UncontrolledTooltip } from 'reactstrap';
import one from "../../../../../../../../assets/images/user/1.jpg";
import three from "../../../../../../../../assets/images/user/3.jpg";
import five from "../../../../../../../../assets/images/user/5.jpg";
import two from "../../../../../../../../assets/images/user/2.png";
import eight from "../../../../../../../../assets/images/user/8.jpg";
import eleven from "../../../../../../../../assets/images/user/11.png";
import timeline3 from "../../../../../../../../assets/images/social-app/timeline-3.png";
import ten from "../../../../../../../../assets/images/user/10.jpg";
import six from "../../../../../../../../assets/images/user/6.jpg";
import fourteen from "../../../../../../../../assets/images/user/14.png";
import four from "../../../../../../../../assets/images/user/4.jpg";
import _ from "lodash";
import { BuckyBarnes, JasonBorne, SarahLoren, AndewJon, JohnyWaston, JohnyWilliam, ComerenDiaz, MutualFriends, ActivityFeed } from "../../../../../../../../constant";
import profileLogoPlaceholder from "../../../../../../../../assets/images/logo/logo-icon.png";

const LeftBar = ({ applicantData, lastProfileItem }) => {

    const [testingDatesOpen, setTestingDatesOpen] = useState(true);
    const [isMutual, setisMutual] = useState(true);
    const [isActivity, setisActivity] = useState(true);
    // previous applications/bids/proposals...
    const [ previouslyApplied, setPreviouslyAppliedState ] = useState([{
        profilePicture: "https://picsum.photos/200/300",
        name: "Jeremy Blong",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Alex Jankins",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Tom Mcdonald",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Kid Inkedslao",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Theo Tomhoas",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Mikey Smikithy",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Timoothy Walkings",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Adam Jones",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Alex Qualoda",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Pochie Notch",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Yelawolf Smelodo",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }, {
        profilePicture: "https://picsum.photos/200/300",
        name: "Yolodanda Smith",
        relatedListingTitle: "Praesent consequat lectus leo, a gravida dui efficitur sit amet. Integer scelerisque molestie elementum. Sed laoreet vestibulum mauris quis dictum"
    }]);
    const calculateStatusType = (num) => {
        switch (num) {
            case 1:
                return "social-online";
                break;
            case 2:
                return "social-offline";
                break;
            case 3:
                return "social-busy";
                break;
            default:
                break;
        }
    }
    return (
        <Fragment>
            <Col xl="12">
                <Card className={"add-shadow-md-custom"}>
                    <CardHeader>
                        <h5 className="mb-0">
                            <Button color="link pl-0" onClick={() => {
                                setTestingDatesOpen(!testingDatesOpen);
                            }}
                                data-toggle="collapse" data-target="#collapseicon5" aria-expanded={testingDatesOpen} aria-controls="collapseicon5"
                                >View General User Information
                            </Button>
                        </h5>
                        <p>You can view the user's entire profile & more by clicking the "View More" button below...</p>
                    </CardHeader>
                    <Collapse isOpen={testingDatesOpen}>
                        <CardBody className="socialprofile filter-cards-view">
                            <Media><Media className="img-50 img-fluid m-r-20 rounded-circle" src={(typeof lastProfileItem !== "undefined" && _.has(lastProfileItem, "link")) ? `${process.env.REACT_APP_ASSET_LINK}/${lastProfileItem.link}` : profileLogoPlaceholder} alt="" />
                                <Media body>
                                    <h6 className="font-primary f-w-600">{applicantData.applicantName}</h6><span className="d-block"><span><i className="fa fa-bell-o"> </i><span className="px-2">FOLLOWERS <span style={{ color: "white" }} className="badge badge-pill badge-info">{(Math.floor(Math.random() * 750) + 1)}</span></span></span></span><span className="d-block"><span><i className="fa fa-bell-o"></i><span className="px-2">FOLLOWING <span style={{ color: "white" }} className="badge badge-pill badge-info">{(Math.floor(Math.random() * 750) + 1)}</span></span></span></span>
                                </Media>
                            </Media>
                            <div className="social-btngroup d-flex">
                                <Button outline className={"btn-square-info text-center"} color="info-2x" type="button">View More</Button>
                            </div>
                            <div className="likes-profile text-center">
                                <h5><span><i className="fa fa-heart font-danger"></i> {`${(Math.floor(Math.random() * 10000) + 1)} total page saves`}</span></h5>
                            </div>
                            <div className="text-center">{`${(Math.floor(Math.random() * 250) + 1)} New Saves This Week`}</div>
                            <div className="customers text-center social-group">
                                <ul>
                                    <li className="d-inline-block">
                                        <Media className="img-40 rounded-circle" src={three} alt="ThirdImg" id="UncontrolledTooltipExample"/>
                                        <UncontrolledTooltip placement="top" target="UncontrolledTooltipExample">
                                            {JohnyWaston}
                                        </UncontrolledTooltip>
                                    </li>
                                    <li className="d-inline-block">
                                        <Media className="img-40 rounded-circle" src={five} alt="FifthImg" id="UncontrolledTooltipExample1" />
                                        <UncontrolledTooltip placement="top" target="UncontrolledTooltipExample1">
                                            {AndewJon}
                                        </UncontrolledTooltip>
                                    </li>
                                    <li className="d-inline-block">
                                        <Media className="img-40 rounded-circle" src={one} alt="FirstImg" id="UncontrolledTooltipExample2" />
                                        <UncontrolledTooltip placement="top" target="UncontrolledTooltipExample2">
                                            {ComerenDiaz}
                                        </UncontrolledTooltip>
                                    </li>
                                    <li className="d-inline-block">
                                        <Media className="img-40 rounded-circle" src={two} alt="secondImg" id="UncontrolledTooltipExample3" />
                                        <UncontrolledTooltip placement="top" target="UncontrolledTooltipExample3">
                                            {BuckyBarnes}
                                        </UncontrolledTooltip>
                                    </li>
                                    <li className="d-inline-block">
                                        <Media className="img-40 rounded-circle" src={eight} alt="eightImg" id="UncontrolledTooltipExample4" />
                                        <UncontrolledTooltip placement="top" target="UncontrolledTooltipExample4">
                                            {JasonBorne}
                                        </UncontrolledTooltip>
                                    </li>
                                    <li className="d-inline-block">
                                        <Media className="img-40 rounded-circle" src={eleven} alt="elevenImg" id="UncontrolledTooltipExample5" />
                                        <UncontrolledTooltip placement="top" target="UncontrolledTooltipExample5">
                                            {ComerenDiaz}
                                        </UncontrolledTooltip>
                                    </li>
                                </ul>
                            </div>
                        </CardBody>
                    </Collapse>
                </Card>
            </Col>
            <Col xl="12">
                <Card className={"add-shadow-md-custom"}><Media className="img-fluid" alt="" src={timeline3} /></Card>
            </Col>
            <Col xl="12">
                <Card className={"add-shadow-md-custom"}>
                    <CardHeader>
                        <h5 className="mb-0">
                            <Button color="link pl-0" onClick={() => setisMutual(!isMutual)}
                                data-toggle="collapse" data-target="#collapseicon6" aria-expanded={isMutual} aria-controls="collapseicon6">Previously Applied Gigs/Jobs/Position's By This User</Button>
                        </h5>
                    </CardHeader>
                    <Collapse isOpen={isMutual}>
                        <CardBody className="social-status filter-cards-view">
                            <Form>
                                <FormGroup className="m-0">
                                    <Input className="form-control-social" type="search" placeholder="Search previous applications(search by 'listing title')..." />
                                </FormGroup>
                            </Form>
                            <Media>
                                <img  className="img-50 rounded-circle m-r-15" src={two} alt="twoImg" />
                                <div className="social-status social-online"></div>
                                <Media body>
                                    <span className="f-w-600 d-block">{BuckyBarnes}</span><span className="d-block">{"winter@gmail.com"}</span>
                                </Media>
                            </Media>
                            {/* social-status different types --- social-online, social-offline, social-busy */}
                            {typeof previouslyApplied !== "undefined" && previouslyApplied.length > 0 ? previouslyApplied.map((applied, index) => {

                                // return data...
                                return (
                                    <Fragment key={index}>
                                        <Media>
                                            <img className="img-50 rounded-circle m-r-15" src={applied.profilePicture} alt="TenImg" />
                                            <div className={`social-status ${calculateStatusType(Math.floor(Math.random() * 3) + 1)}`}></div>
                                            <Media body>
                                                <span className="f-w-600 d-block">{applied.name}</span><span className="d-block">{applied.relatedListingTitle}</span>
                                            </Media>
                                        </Media>
                                    </Fragment>
                                );
                            }) : <Fragment></Fragment>}
                        </CardBody>
                    </Collapse>
                </Card>
            </Col>
            <Col xl="12">
                <Card className={"add-shadow-md-custom"}>
                    <CardHeader>
                        <h5 className="mb-0">
                            <Button color="link pl-0" onClick={() => setisActivity(!isActivity)}
                                data-toggle="collapse" data-target="#collapseicon14" aria-expanded={isActivity} aria-controls="collapseicon14">{ActivityFeed}</Button>
                        </h5>
                    </CardHeader>
                    <Collapse isOpen={isActivity}>
                        <CardBody className="social-status filter-cards-view">
                            <Media><Media className="img-50 rounded-circle m-r-15" src={ten} alt="tenImg" />
                                <Media body>
                                    <span className="f-w-600 d-block">{AndewJon}</span>
                                    <p>{"Commented on Shaun Park's"} <a href="#javascript">{"Photo"}</a></p><span className="light-span">{"20 min Ago"}</span>
                                </Media>
                            </Media>
                            <Media><Media className="img-50 rounded-circle m-r-15" src={three} alt="threeImg" />
                                <Media body>
                                    <span className="f-w-600 d-block">{JohnyWaston}</span>
                                    <p>{"Commented on Shaun Park's"} <a href="#javascript">{"Photo"}</a></p><span className="light-span">{"1 hour Ago"}</span>
