                break;
        }
    }

    return renderMainContentPassedConditional(type);
};


const SlideUpPaneAuctionPurchaseSelectionData = ({ typeOfListing, setIsTourOpenStatus, selected, shiftCoreStyles, previouslySavedSoftwareData, saveSoftwareListingInfo, onSubmit, onErrorMainForm, scrollToTourWrapper }) => {

