import React, { Fragment } from "react";

const preparePriceString = (item) => {
    const type = item.auctionPurchaseType;
    // AUCTION LISTINGS (((ONLY)))
    if (type.includes("auction")) {
        if (type === "auction-ONLY") {
            const bids = item.bids;
            const currentPrice = item.currentBidPrice;
            return (
                <Fragment>
                    {`${bids.length} current bid's at $${currentPrice} (USD)`}
                    <p className={"pricing-details-custom-inner"}>{"Auction ONLY"}</p>
                </Fragment>
            );
        } else if (type === "auction-AND-buy-it-now") {
            const reservePrice = item.auctionPriceRelatedData.reservePrice;
            const buyitnowPrice = item.auctionPriceRelatedData.buyItNowData.buyItNowPrice;
            const currentPrice = item.currentBidPrice;
            return (
                <Fragment>
                    <p><code className={"custom-price-tag"}>{`$${currentPrice.toFixed(2)}`}</code>{" (Auction) with "}<code className={"custom-price-tag"}>{item.bids.length}</code> <strong style={{ color: "black", fontStyle: "italic" }}>current bids</strong></p>
                    <p><code className={"custom-price-tag"}>{`$${buyitnowPrice.toFixed(2)}`}</code>{" (Buy-it-now)"} - <code className={"custom-price-tag"}>{`$${reservePrice.toFixed(2)}`}</code> <strong style={{ color: "black", fontStyle: "italic" }}>{"reserved price"}</strong></p>
                </Fragment>
            );
        } 
        // BUY IT NOW LISTINGS (((ONLY)))
    } else {
        // create buy-it-now variable
        const buyitnow = item.auctionPriceRelatedData.buyItNowPrice;
        // buy it now OR best offer
        if (type === "buy-it-now-OR-best-offer") {
            // create minoffer variable for string below
            const minOffer = item.auctionPriceRelatedData.offerFeatureData.minimumRequiredOfferValue;
            // RETURN both best - offer buy it now
            return (
                <Fragment>
                    {`$${buyitnow.toFixed(2)} (Buy-it-now)`}
                    <p className={"pricing-details-custom-inner"}>{`$${minOffer.toFixed(2)} (Min-offer)`}</p>
                </Fragment>
            );
        } else if (type === "buy-it-now-ONLY") {
            return (
                <Fragment>
                    {`$${buyitnow.toFixed(2)} (buy-it-now) ONLY`}
                    <p className={"pricing-details-custom-inner"}>{"Buy-it-now ONLY"}</p>
                </Fragment>
            );
        }
    }
}

export default {
    preparePriceString
};
