import React, { useEffect, useState, useRef, useMemo } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { Col, Button, Form, Label, Input, FormGroup, Card, CardHeader, CardBody, Media, Row } from 'reactstrap';
import { State, City, Zip, SubmitForm } from '../../../../../../../constant';
import "./styles.css";
import Select from 'react-select';
import _ from "lodash";
import { languageList } from "./helpers/programmingLanguageList/languageList.js";
import SimpleMDE from "react-simplemde-editor";

const categoryOptions = [
    { value: 'malware-related', label: 'Malware Related Codes' },
    { value: 'phishing', label: 'Phishing' },
    { value: "cross-site-scripting-xss", label: "Cross Site Scripting (XSS)" },
    { value: "denial-of-service", label: "Denial Of Service (DDoS)" },
    { value: "session-hijacking-man-in-middle", label: "Session Hijacking & Man-In-Middle Attacks" },
    { value: "macro-malware-in-documents", label: "Macro-Malware In Documents" },
    { value: "iot-attack", label: "IoT Attack" },
    { value: "clickjacking-ui-redress", label: "Clickjacking/UI Redress" },
    { value: "dns-spoofing", label: "DNS Spoofing" },
    { value: "watering-hole-attack", label: "Watering Hole Attack" },
    { value: "keylogger-attack", label: "Keylogger Attack" },
    { value: "bruteforce-attack", label: "Brute-Force Attack" },
    { value: "dictionary-attack", label: "Dictionary Attack" },
    // easy/lazy BELOW
    { value: "credential-reuse", label: "Credential Reuse" },
    { value: 'sql-injection-attack', label: 'SQL Injection Attack' },
    { value: "fake-wap", label: "Fake WAP" },
    { value: "bait-and-switch", label: "Bait & Switch" },
    { value: "browser-locker", label: "Browser Locker" },
    // easy/lazy ABOVE
];


const PageOneMainHelper = (props) => {

    let categorySelectRef = useRef(null);

    const { register, handleSubmit, control, trigger, setValue, setError, clearErrors, formState: { errors }} = useForm({
        mode: "onTouched",
        reValidateMode: "onBlur",
        delayError: 500
    });

    const [validateClass , setValidateClass] = useState(false);
    const [ data, setData ] = useState({});
    const [ showSelectOneError, setSelectOneErrorStatus ] = useState(false);


    const onSubmit = (e, data) => {

        console.log("submitting (onSubmit function)...!!");

        e.preventDefault();

        if (data !== '') {
            alert('You submitted the form and stuff!');
        } else {
            errors.showMessages();
        }
    };
    const onError = (errors, e) => {
        console.log("error submitting...!");

        console.log(errors, e);

        // for (const key in errors) {

        //     const error = errors[key];

        //     console.log(error);

        //     switch (error) {
        //         case "category":
        //             if (showSelectOneError === true) {
        //                 clearErrors("category");
        //             }
        //             break;

        //         default:
        //             break;
        //    }
        // }
    };
    const handleInputChange = (e) => {
        const { name, value } = e.target;

        clearErrors(name);

        setData(prevState => {
            return {
                ...prevState,
                [name]: value
            }
        });

        return value;
    };
    console.log("data", data);

    useEffect(() => {
        // const watchCategory = watch("category", false);

        // console.log("watchCategory : ", watchCategory);
    }, []);

    const menuClosedSelectInput = () => {
        if (_.has(data, "category")) {
            setSelectOneErrorStatus(true);
        } 
    }

    const autofocusNoSpellcheckerOptions = useMemo(() => {
        return {
          autofocus: false,
          spellChecker: false,
        };
    }, []);

    console.log("errors", errors);

    console.log("categorySelectRef : ", categorySelectRef);
    return (
        <div>
            <Form className={`needs-validation tooltip-validation ${validateClass ? 'validateClass' : ''}`} noValidate="" onSubmit={handleSubmit(onSubmit, onError)}>
                <div className="form-row">
                <Col md="6 mb-3">
                    <Label>Listing Title (Be As Specific As Possible)</Label>
                    <input {...register("listingTitle", { required: {
                        value: true,
                        message: "You MUST enter a value between 15-75 characters"
                    }, minLength: {
                        value: 15,
                        message: "You must enter AT Least 15 characters"
                    }, maxLength: {
                        value: 75,
                        message: "You may ONLY enter 75 characters or less"
                    }})} className="form-control" name="listingTitle" type="text" placeholder="Listing title" onChange={handleInputChange} value={data.listingTitle} />
                    {/* 'Listing title is required & total length must be less than 50 charectors' */}
                    {errors.listingTitle ? <span className="span-tooltip">{errors.listingTitle.message}</span> : null}
                    <div className="valid-feedback">{"Looks good!"}</div>
                </Col>
                <Col md="6 mb-3">
                    <Label>Type Of Hack Being Sold</Label>
                    <Controller
                        control={control}
                        name="category"
                        {...register("category", { required: true, onBlur: () => {
                            console.log("onBlur...!");

                            if (showSelectOneError === true) {
                                alert("category HAS value")
                            } else {
                                clearErrors("category");
                            }
                        }})}
                        render={({ field }) => (
                            <Select
                                {...field}
                                ref={categorySelectRef}
                                autoBlur={true}
                                defaultValue={null}
                                onMenuClose={menuClosedSelectInput}
                                value={data.selectedOption}
                                onChange={(selectedOption) => {
                                    setData(prevState => {
                                        return {
                                            ...prevState,
                                            category: selectedOption
                                        }
                                    });
                                    setValue('category', selectedOption, { shouldValidate: false })
                                    categorySelectRef.current.blur();
                                }}
                                options={categoryOptions}
                            />
                        )}
                    />
                    {errors.category ? <span className="span-tooltip">{errors.category && 'Category is required'}</span> : null}
                    <div className="valid-feedback">{"Looks good!"}</div>
                </Col>
                {/* <Col md="4 mb-3">
                    <Label>{Username}</Label>
                    <InputGroup>
                    <InputGroupAddon addonType="prepend">
                        <InputGroupText >{"@"}</InputGroupText>
                        </InputGroupAddon>
                    <Input className="form-control"  name="userName" type="text" placeholder="Username" aria-describedby="inputGroupPrepend" {...register(("testing"), { required: true })} />
                    <span>{errors.lastName && 'User name is required'}</span>
                    <div className="invalid-feedback">{"Please choose a username."}</div>
                    </InputGroup>
                </Col> */}
                </div>
                <div className="form-row">
                <Col md="6 mb-3">
                    <Label>{City}</Label>
                    <Input className="form-control"  name="city" type="text" placeholder="City" {...register(("testing"), { required: true })} onChange={handleInputChange} />
                    <span>{errors.city && 'Please provide a valid city'}</span>
                    <div className="invalid-feedback">{"Please provide a valid city."}</div>
                </Col>
                <Col md="3 mb-3">
                    <Label>{State}</Label>
                    <Input className="form-control"  name="state" type="text" placeholder="State" {...register(("testing"), { required: true })} onChange={handleInputChange} />
                    <span>{errors.state && 'Please provide a valid state.'}</span>
                    <div className="invalid-feedback">{"Please provide a valid state."}</div>
                </Col>
                <Col md="3 mb-3">
                    <Label>{Zip}</Label>
                    <Input className="form-control"  name="zip" type="text" placeholder="Zip" {...register(("testing"), { required: true })} onChange={handleInputChange} />
                    <span>{errors.zip && 'Please provide a valid zip.'}</span>
                    <div className="invalid-feedback">{"Please provide a valid zip."}</div>
                </Col>
                </div>
                <FormGroup>
                <div className="form-check">
                    <div className="checkbox p-0">
                    <Input className="form-check-input" id="invalidCheck3" type="checkbox" onChange={handleInputChange} />
                    <Label className="form-check-label" htmlFor="invalidCheck3">{"Agree to terms and conditions"}</Label>
                    </div>
                </div>
                </FormGroup>
                <hr />
                <Col sm="12" xl="6 xl-100 box-col-12">
                    <div className="card height-equal">
                        <CardHeader>
                            <h5>Supporting Your Item ("Item Support")</h5><span>{"For Create inline megaoption add"} <code>{".mega-vertical"}</code> {"class in form tag"}</span>
                        </CardHeader>
                        <CardBody>
                            <Form className="mega-vertical">
                                <Row>
                                    <Col sm="12">
                                    <p className="mega-title m-b-5">ABC Seller</p>
                                    </Col>
                                    <Col sm="6">
                                    <Card>
                                        <Media className="p-20">
                                        <div className="radio radio-primary mr-3">
                                            <Input id="radio23" type="radio" name="radio1" value="option1" />
                                            <Label for="radio23"></Label>
                                        </div>
                                        <Media body>
                                            <h6 className="mt-0 mega-title-badge">ABC Seller<span className="badge badge-primary pull-right digits">{"50 INR"}</span></h6>
                                            <p>{"Estimated 10 to 15 Day Shipping ( Duties end tax may be due delivery )"}</p>
                                        </Media>
                                        </Media>
                                    </Card>
                                    </Col>
                                    <Col sm="6">
                                    <Card>
                                        <Media className="p-20">
                                        <div className="radio radio-secondary mr-3">
                                            <Input id="radio24" type="radio" name="radio1" value="option1" />
                                            <Label for="radio24"></Label>
                                        </div>
                                        <Media body>
                                            <h6 className="mt-0 mega-title-badge">ABC Seller <span className="badge badge-secondary pull-right digits">{"100 INR"}</span></h6>
                                            <p>{"Estimated 10 to 12 Day Shipping ( Duties end tax may be due delivery )"}</p>
                                        </Media>
                                        </Media>
                                    </Card>
                                    </Col>
                                    <Col sm="6">
                                    <Card>
                                        <Media className="p-20">
                                        <div className="radio radio-success mr-3">
                                            <Input id="radio25" type="radio" name="radio1" value="option1" />
                                            <Label for="radio25"></Label>
                                        </div>
                                        <Media body>
                                            <h6 className="mt-0 mega-title-badge">ABC Seller <span className="badge badge-success pull-right digits">{"80 INR"}</span></h6>
                                            <p>{"Estimated 3 to 5 Day Shipping ( Duties end tax may be due delivery )"}</p>
                                        </Media>
                                        </Media>
                                    </Card>
                                    </Col>
                                    <Col sm="6">
                                    <Card>
                                        <Media className="p-20">
                                        <div className="radio radio-info mr-3">
                                            <Input id="radio5" type="radio" name="radio1" value="option1" />
                                            <Label for="radio5"></Label>
                                        </div>
                                        <Media body>
                                            <h6 className="mt-0 mega-title-badge">ABC Seller <span className="badge badge-info pull-right digits">{Free}</span></h6>
                                            <p>{"Estimated 3 to 5 Day Shipping ( Duties end taxes may be due upon delivery )"}</p>
                                        </Media>
                                        </Media>
                                    </Card>
                                    </Col>
                                </Row>
                            </Form>
                        </CardBody>
                    </div>
                </Col>
                <hr />
                <Row className="bottom-row-spacing">
                    <Col md="12" sm="12" xl="12" lg="12"> 
                    <Controller
                        control={control}
                        name="description"
                        value={data.description}
                        {...register("description", { required: true, onBlur: () => {
                            console.log("onBlur...!");

                            setTimeout(() => {
                                if ((typeof data.description !== "undefined" && data.description.length <= 325) && (data.description.length >= 0)) {
                                    console.log("one ran!");

                                    setError("description", {
                                        type: "manual",
                                        message: "You must enter AT least 325 characters or more to proceed",
                                    });
                                } else if ((typeof data.description !== "undefined" && data.description.length >= 1250)) {
                                    console.log("two ran!");

                                    setError("description", {
                                        type: "manual",
                                        message: "You may only enter up to a max of 1250 characters",
                                    });
                                } else {
                                    console.log("three ran!");

                                    setError("description", {
                                        type: "manual",
                                        message: "You must enter between 325-1250 characters to proceed",
                                    });
                                }
                            }, 1250)
                        }})}
                        render={({ field, ref }) => (
                            <SimpleMDE
                                ref={ref}
                                {...field}
                                placeholder={"Enter your text here... (You can use MARKUP & various text styling tools with the bar above)"}
                                name="description"
                                id="editor_container"
                                onChange={(value) => {
                                    clearErrors("description");

                                    setData(prevState => {
                                        return {
                                            ...prevState,
                                            description: value
                                        }
                                    });

                                    return value;
                                }}
                                value={data.description}
                                options={autofocusNoSpellcheckerOptions}
                            />
                        )}
                    />
                    </Col>
                    <Col md="12" sm="12" xl="12" lg="12">
                        {errors.description ? <span className="span-tooltip">{errors.description.message}</span> : null}
                    </Col>
                </Row>
                <Button color="primary"  type="submit" onClick={() => setValidateClass(!validateClass)}>{SubmitForm}</Button>
            </Form>
        </div>
    );
}

export default PageOneMainHelper;
