import React, { Fragment, useState, useEffect } from 'react';
import "./styles.css";
import { Container, Card, CardHeader, CardBody, CardFooter, Row, Col } from "reactstrap";
import Slider from "react-slick";
import Breadcrumb from '../../../../../layout/breadcrumb';
import ReactPlayer from "react-player";
import moment from "moment";


const settings = {
    dots: true,
    infinite: true,
    draggable: true,
    speed: 500,
    slidesToShow: 4,
    slidesToScroll: 1,
    responsive: [
        {
          breakpoint: 1350,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 675,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
};

const randomDate = (start, end) => {
    return new Date(start.getTime() + Math.random() * (end.getTime() - start.getTime()));
}

const ViewAllTutorialCoursesHelper = (props) => {

    const [ data, setData ] = useState({
        dataChunkOne: new Array(10).fill({
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
            name: "JeremyAlexanderBlong",
            views: 54504,
            date: randomDate(new Date(2022, 0, 1), new Date())
        }),
        dataChunkTwo: new Array(10).fill({
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
            name: "TheHackingStreamer1092",
            views: 54504,
            date: randomDate(new Date(2022, 0, 1), new Date())
        }),
        dataChunkThree: new Array(10).fill({
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
            name: "MrBeastSucks",
            views: 54504,
            date: randomDate(new Date(2022, 0, 1), new Date())
        }),
        dataChunkFour: new Array(10).fill({
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
            name: "AdamJewler19",
            views: 54504,
            date: randomDate(new Date(2022, 0, 1), new Date())
        }),
        dataChunkFive: new Array(10).fill({
            title: "Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt",
            name: "JohnnyKnox2948",
            views: 54504,
            date: randomDate(new Date(2022, 0, 1), new Date())
        })
    });
    return (
        <Fragment>
            <Breadcrumb parent="View FREE Tutorial Video(s)" title="Learn New Hacking Skills Today.." />
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className='shadow'>
                            <CardBody>
                                <Row>
                                    <h3 className='text-left'>Malware Attack Tutorial's</h3>
                                    <p className='lead'>This is one of the most common types of cyberattacks. “Malware” refers to malicious software viruses including worms, spyware, ransomware, adware, and trojans. </p>
                                </Row>
                                <Row style={{ marginTop: "17.5px" }}>
                                    <Slider {...settings}>
                                        {typeof data.dataChunkOne !== "undefined" && data.dataChunkOne.length > 0 ? data.dataChunkOne.map((element, index) => {
                                            return (
                                                <Fragment key={index}>
                                                    <Col className='custom-card-chunk-tutorial' xl="3" sm="12" lg="3" md="3">
                                                        <Card className="card-tutorial-video shadow">
                                                            <div className="faq-image product-img">
                                                                <video 
                                                                    onMouseOver={event => event.target.play()}
                                                                    onMouseOut={event => event.target.pause()} 
                                                                    className={"tutorial-video-player"} 
                                                                    key={index}
                                                                >
                                                                    <source src={require("../../../../../assets/video/hacking-1.mp4")} />
                                                                </video>
                                                            </div>
                                                            <CardBody>
                                                                <h6>{element.title}</h6>
                                                                <hr />
                                                                <p className={'muted-text-color'}>{element.name}</p>
                                                                <p className={"muted-text-color"}>{element.views} Views - {moment(element.date).fromNow()}</p>
                                                            </CardBody>
                                                        </Card>
                                                    </Col>
                                                </Fragment>
                                            );
                                        }) : null}
                                    </Slider>
                                </Row>
                                <Row>
                                    <h3 className='text-left'>Phishing Attack Tutorial's</h3>
                                    <p className='lead'>Phishing attacks are one of the most prominent widespread types of cyberattacks. It is a type of social engineering attack wherein an attacker impersonates to be a trusted contact and sends the victim fake mails. </p>
                                </Row>
                                <Row style={{ marginTop: "75px" }}>
                                    <Slider {...settings}>
                                        {typeof data.dataChunkTwo !== "undefined" && data.dataChunkTwo.length > 0 ? data.dataChunkTwo.map((element, index) => {
                                            return (
                                                <Fragment key={index}>
                                                    <Col className='custom-card-chunk-tutorial' xl="3" sm="12" lg="3" md="3">
                                                        <Card className="card-tutorial-video shadow">
                                                            <div className="faq-image product-img">
                                                                <video 
                                                                    onMouseOver={event => event.target.play()}
                                                                    onMouseOut={event => event.target.pause()} 
                                                                    className={"tutorial-video-player"} 
                                                                    key={index}
                                                                >
                                                                    <source src={require("../../../../../assets/video/hacking-2.mp4")} />
                                                                </video>
                                                            </div>
                                                            <CardBody>
                                                                <h6>{element.title}</h6>
                                                                <hr />
                                                                <p className={'muted-text-color'}>{element.name}</p>
                                                                <p className={"muted-text-color"}>{element.views} Views - {moment(element.date).fromNow()}</p>
                                                            </CardBody>
                                                        </Card>
                                                    </Col>
                                                </Fragment>
                                            );
                                        }) : null}
                                    </Slider>
                                </Row>
                                <Row>
                                    <h3 className='text-left'>Password Attack Tutorial's</h3>
                                    <p className='lead'>It is a form of attack wherein a hacker cracks your password with various programs and password cracking tools like Aircrack, Cain, Abel, John the Ripper, Hashcat, etc. There are different types of password attacks like brute force attacks, dictionary attacks, and keylogger attacks.</p>
                                </Row>
                                <Row style={{ marginTop: "75px" }}>
                                    <Slider {...settings}>
                                        {typeof data.dataChunkThree !== "undefined" && data.dataChunkThree.length > 0 ? data.dataChunkThree.map((element, index) => {
                                            return (
                                                <Fragment key={index}>
                                                    <Col className='custom-card-chunk-tutorial' xl="3" sm="12" lg="3" md="3">
                                                        <Card className="card-tutorial-video shadow">
                                                            <div className="faq-image product-img">
                                                                <video 
                                                                    onMouseOver={event => event.target.play()}
                                                                    onMouseOut={event => event.target.pause()} 
                                                                    className={"tutorial-video-player"} 
                                                                    key={index}
                                                                >
                                                                    <source src={require("../../../../../assets/video/hacking-3.mp4")} />
                                                                </video>
                                                            </div>
                                                            <CardBody>
                                                                <h6>{element.title}</h6>
                                                                <hr />
                                                                <p className={'muted-text-color'}>{element.name}</p>
                                                                <p className={"muted-text-color"}>{element.views} Views - {moment(element.date).fromNow()}</p>
                                                            </CardBody>
                                                        </Card>
                                                    </Col>
                                                </Fragment>
                                            );
                                        }) : null}
                                    </Slider>
                                </Row>
                                <Row>
                                    <h3 className='text-left'>Man-in-the-Middle Attack Tutorial's</h3>
                                    <p className='lead'>A Man-in-the-Middle Attack (MITM) is also known as an eavesdropping attack. In this attack, an attacker comes in between a two-party communication, i.e., the attacker hijacks the session between a client and host. By doing so, hackers steal and manipulate data.</p>
                                </Row>
                                <Row style={{ marginTop: "75px" }}>
                                    <Slider {...settings}>
                                        {typeof data.dataChunkFour !== "undefined" && data.dataChunkFour.length > 0 ? data.dataChunkFour.map((element, index) => {
                                            return (
                                                <Fragment key={index}>
                                                    <Col className='custom-card-chunk-tutorial' xl="3" sm="12" lg="3" md="3">
                                                        <Card className="card-tutorial-video shadow">
                                                            <div className="faq-image product-img">
                                                                <video 
                                                                    onMouseOver={event => event.target.play()}
                                                                    onMouseOut={event => event.target.pause()} 
                                                                    className={"tutorial-video-player"} 
                                                                    key={index}
                                                                >
                                                                    <source src={require("../../../../../assets/video/hacking-4.mp4")} />
                                                                </video>
                                                            </div>
                                                            <CardBody>
                                                                <h6>{element.title}</h6>
                                                                <hr />
                                                                <p className={'muted-text-color'}>{element.name}</p>
                                                                <p className={"muted-text-color"}>{element.views} Views - {moment(element.date).fromNow()}</p>
                                                            </CardBody>
                                                        </Card>
                                                    </Col>
                                                </Fragment>
                                            );
                                        }) : null}
                                    </Slider>
                                </Row>
                                <Row>
                                    <h3 className='text-left'>SQL Injection Attack Tutorial's</h3>
                                    <p className='lead'>A Structured Query Language (SQL) injection attack occurs on a database-driven website when the hacker manipulates a standard SQL query. It is carried by injecting a malicious code into a vulnerable website search box, thereby making the server reveal crucial information.</p>
                                </Row>
                                <Row style={{ marginTop: "75px" }}>
                                    <Slider {...settings}>
                                        {typeof data.dataChunkFive !== "undefined" && data.dataChunkFive.length > 0 ? data.dataChunkFive.map((element, index) => {
                                            return (
                                                <Fragment key={index}>
                                                    <Col className='custom-card-chunk-tutorial' xl="3" sm="12" lg="3" md="3">
                                                        <Card className="card-tutorial-video shadow">
                                                            <div className="faq-image product-img">
                                                                <video 
                                                                    onMouseOver={event => event.target.play()}
                                                                    onMouseOut={event => event.target.pause()} 
                                                                    className={"tutorial-video-player"} 
                                                                    key={index}
                                                                >
                                                                    <source src={require("../../../../../assets/video/hacking-5.mp4")} />
                                                                </video>
                                                            </div>
                                                            <CardBody>
                                                                <h6>{element.title}</h6>
                                                                <hr />
                                                                <p className={'muted-text-color'}>{element.name}</p>
                                                                <p className={"muted-text-color"}>{element.views} Views - {moment(element.date).fromNow()}</p>
                                                            </CardBody>
                                                        </Card>
                                                    </Col>
                                                </Fragment>
                                            );
                                        }) : null}
                                    </Slider>
                                </Row>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}

export default ViewAllTutorialCoursesHelper;
