import React, { Fragment, useState } from 'react';
import "./styles.css";
import Breadcrumb from '../../../../../layout/breadcrumb';
import { Container, Card, CardBody, CardHeader, Row, Col } from "reactstrap";
import StepZilla from "react-stepzilla";
import PageOneVideoCreationHelper from "./formSteps/pageOne/formPageOne.js";
import PageTwoVideoCreationHelper from "./formSteps/pageTwo/pageTwoSection.js";
import { useForm, Controller } from 'react-hook-form';


const CreateTutorialCourseContentHelper = (props) => {

    const { register, handleSubmit, control, reset, getValues, setValue, setError, clearErrors, formState: { errors }} = useForm({
        mode: "onTouched",
        reValidateMode: "onBlur"
    });

    const onSubmit = (data, index) => {

        console.log("data and index", data, index);

        if (data !== '') {
            console.log("Success!");

            props.jumpToStep(index);
        } else {
            errors.showMessages();
        }
    };


    const steps = [
        { name: 'Core Details', component: <PageOneVideoCreationHelper register={register} handleSubmit={handleSubmit} getValues={getValues} setValue={setValue} errors={errors} control={control} props={props} onSubmit={onSubmit} /> },
        { name: "Thumbnail/Preview", component: <PageTwoVideoCreationHelper register={register} handleSubmit={handleSubmit} getValues={getValues} setValue={setValue} errors={errors} control={control} props={props} onSubmit={onSubmit} /> }
    ];

    return (
        <Fragment>
            <Breadcrumb parent="Tutorial Content/Courses - Informational Video(s)" title="Create A FREE Tutorial Video/Content" />
            <Container fluid={true}>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className='shadow'>
                            <CardHeader className={"b-l-primary b-r-primary"}>
                                <h3>Create a tutorial video/content - Select and upload your data via the form logic below (These are ONLY FREE video's up-to 30 minutes per video)</h3>
                            </CardHeader>
                            <CardBody>
                                <StepZilla 
                                    steps={steps} 
                                    showSteps={true} 
                                    showNavigation={false} 
                                    stepsNavigation={false}
                                    onStepChange={(index) => {
                                        console.log("index", index);
                                    }}
                                />
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </Container>
        </Fragment>
    );
}

export default CreateTutorialCourseContentHelper;
