import React, { Fragment, useState, useEffect } from 'react';
import "./styles.css";
import ReactPlayer from 'react-player';


const Loader = (props) => {

    const [show, setShow] = useState(true);

    useEffect(() => {
        const timeout = setTimeout(() => {
            setShow(false)
            }, 3000);

        return () => {
            clearTimeout(timeout);
            }

    },[show]);

    return (
        <Fragment>
            <ReactPlayer playing={true} loop={true} muted={true} height={"100%"} width={"100%"} className={"fullscreen-loader"} wrapper={"div"} url={require("../../assets/video/animated-loading.mov")} />
        </Fragment>
    );
}

export default Loader;
