import React, { Component } from 'react';
import { WithContext as ReactTags } from 'react-tag-input';
import { connect } from "react-redux";
import _ from "lodash";

const KeyCodes = {
  comma: 188,
  enter: [10, 13],
};

const delimiters = [...KeyCodes.enter, KeyCodes.comma];

class HashtagCreationTutorialVideoCourseHelper extends Component {
    constructor(props) {
        super(props);

        this.state = {
            tags: [],
            suggestions: []
        };
    }

    handleDelete = (i) => {
        const { tags } = this.state;

        this.setState({
            tags: tags.filter((tag, index) => index !== i),
        }, () => {
            this.props.saveListingData({
                ...this.props.previousData,
                hashtags: this.state.tags
            })
        });
    }
    handleAddition = (tag) => {
        this.setState(state => {
            return { tags: [...state.tags, tag] }
        }, () => {
            this.props.saveListingData({
                ...this.props.previousData,
                hashtags: this.state.tags
            })
        });
    }

    handleDrag = (tag, currPos, newPos) => {
        const tags = [...this.state.tags];
        const newTags = tags.slice();

        newTags.splice(currPos, 1);
        newTags.splice(newPos, 0, tag);

        // re-render
        this.setState({ tags: newTags }, () => {
            this.props.saveListingData({
                ...this.props.previousData,
                hashtags: newTags
            })
        });
    }
    render() {
        const { tags, suggestions } = this.state;
        return (
            <div>
                <ReactTags 
                    tags={tags}
                    classNames={{
                        tags: 'tagsClass',
                        tagInput: 'tagInputClass',
                        tagInputField: 'form-control',
                        selected: 'selectedClass',
                        tag: 'badge badge-primary',
                        remove: 'removeClass',
                        suggestions: 'suggestionsClass',
                        activeSuggestion: 'activeSuggestionClass'
                    }}
                    maxLength={10}
                    suggestions={suggestions}
                    handleDelete={this.handleDelete}
                    handleAddition={this.handleAddition}
                    handleDrag={this.handleDrag}
                    delimiters={delimiters} />
            </div>
        )
    }
};

const mapStateToProps = (state) => {
    return {

    }
}
export default connect(mapStateToProps, {  })(HashtagCreationTutorialVideoCourseHelper);
