import React, { Component, Fragment } from 'react';
import "./styles.css";
import Breadcrumb from '../../../../../layout/breadcrumb';
import { Container, Row, Col, Card, CardBody, Media, Button, Badge, CardHeader, Input, InputGroup, ListGroupItem, ListGroup, FormGroup, Label } from 'reactstrap';
import helpers from "./helpers/miscFunctions.js";
import { connect } from "react-redux";
import moment from "moment";

const { TimelineHelper } = helpers;

const ApplyAsHackerEmployerListingHelper = ({ userData }) => {
    const renderSentence = (type, val) => {
        return <p className="mb-1 listitem-sub-text-custom">Your {type} <strong style={{ color: "#a927f9" }}>{val}</strong> will be submitted to the employer...</p>;
    }
    const viewRemainderOfIncludedFields = (alreadyIncludedArray) => {
        console.log("alreadyIncludedArray arr... : ", alreadyIncludedArray)
    }
    const { username, firstName, lastName, registrationDate, completedJobs } = userData;
    return (
        <Fragment>
            <Breadcrumb parent={"Apply To Employer Listing"} title={"Submit Live Application & Apply!"} />
            <Container className="container-default" fluid={true}>
                <Row style={{ paddingTop: "25px " }}>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <Card className="custom-card-inner customized-top-card">
                            <CardHeader className="b-l-primary">
                                <h5>Apply As A Hacker - Submit Hack Request!</h5>
                            </CardHeader>
                            <CardBody>
                                <p>
                                    {"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been"}
                                    {"the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley"}
                                    {"of type and scrambled. Lorem Ipsum is simply dummy text of the printing and typesetting"}
                                    {"industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an"}
                                    {"unknown printer took a galley of type and scrambled."}
                                </p>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
                <Row>
                    <Col sm="12" md="6" lg="6" xl="6">
                        <Card className="custom-card-inner custom-card-inner-customized">
                            <CardHeader className="b-l-info">
                                <h5>Type Of Data That'll Be Automatically Submitted</h5>
                            </CardHeader>
                            <CardBody>
                                <ListGroup>
                                    <ListGroupItem className="list-group-item-action flex-column align-items-start" >
                                        <div className="d-flex w-100 justify-content-between">
                                        <h5 className="mb-1 listitem-header-custom">{"Username"}</h5><small>{"Will be submitted to employer"}</small>
                                        </div>
                                        <hr />
                                        {renderSentence("Username", username)}
                                    </ListGroupItem>
                                    <ListGroupItem className="list-group-item-action flex-column align-items-start" >
                                        <div className="d-flex w-100 justify-content-between">
                                        <h5 className="mb-1 listitem-header-custom">{"First Name & Last Name (Together)"}</h5><small>{"Will be submitted to employer"}</small>
                                        </div>
                                        <hr />
                                        {renderSentence("Full Name", `${firstName} ${lastName}`)}
                                    </ListGroupItem>
                                    <ListGroupItem className="list-group-item-action flex-column align-items-start" >
                                        <div className="d-flex w-100 justify-content-between">
                                        <h5 className="mb-1 listitem-header-custom">{"Completed Job Count"}</h5><small>{"Will be submitted to employer"}</small>
                                        </div>
                                        <hr />
                                        {renderSentence("Completed Job Count", completedJobs)}
                                    </ListGroupItem>
                                    <ListGroupItem className="list-group-item-action flex-column align-items-start" >
                                        <div className="d-flex w-100 justify-content-between">
                                        <h5 className="mb-1 listitem-header-custom">{"Created Account (from ago...)"}</h5><small>{"Will be submitted to employer"}</small>
                                        </div>
                                        <hr />
                                        {renderSentence("Registration date of", moment(registrationDate).fromNow())}
                                    </ListGroupItem>
                                    <ListGroupItem className="list-group-item-action flex-column align-items-start" >
                                        <div className="d-flex w-100 justify-content-between">
                                        <h5 className="mb-1 listitem-header-custom">{"Created Account (from ago...)"}</h5><small>{"Will be submitted to employer"}</small>
                                        </div>
                                        <hr />
                                        <Button style={{ width: "100%" }} className={"btn-pill btn-air-info"} onClick={() => {
                                            // View ALL included fields...
                                            viewRemainderOfIncludedFields(["username", "firstName", "lastName", "registrationDate", "completedJobs"]);
                                        }} outline color="info-2x">Apply to this listing (As a hacker)</Button>
                                    </ListGroupItem>
                                </ListGroup>
                            </CardBody>
                        </Card>
                    </Col>
                    <Col sm="12" md="6" lg="6" xl="6">
                        <Card className="custom-card-inner custom-card-inner-customized">
                            <CardHeader className="b-l-info">
                                <h5>Core Data - Core Required/Requested Data</h5>
                            </CardHeader>
                            <CardBody>
                                <p>
                                    {"Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been"}
                                    {"the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley"}
                                    {"of type and scrambled. Lorem Ipsum is simply dummy text of the printing and typesetting"}
                                    {"industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an"}
                                    {"unknown printer took a galley of type and scrambled."}
                                </p>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
                <Row>
                    <Col sm="12" md="12" lg="12" xl="12">
                        <TimelineHelper />
                    </Col>
                </Row>
            </Container>
        </Fragment>
    )
}
const mapStateToProps = (state) => {
    return {
        userData: state.auth.data
    }
}
export default connect(mapStateToProps, {  })(ApplyAsHackerEmployerListingHelper);
